server/auth.ts.content:
import jwt from 'jsonwebtoken';
import bcrypt from 'bcryptjs';
import { Request, Response, NextFunction } from 'express';

const JWT_SECRET = process.env.JWT_SECRET || 'your-super-secret-jwt-key-change-in-production';
const SALT_ROUNDS = 12;

export interface ChurchUserPayload {
  id: string;
  churchId: string;
  email: string;
  role: string;
  firstName: string;
  lastName: string;
}

export interface AuthenticatedRequest extends Request {
  user?: ChurchUserPayload;
  churchId?: string;
}

// JWT token utilities
export const generateToken = (payload: ChurchUserPayload, expiresIn: string = '7d'): string => {
  return jwt.sign(payload, JWT_SECRET, { expiresIn });
};

// Generate extended token for kiosk sessions  
export const generateKioskToken = (payload: ChurchUserPayload, kioskTimeoutMinutes: number): string => {
  const extendedMinutes = Math.max(kioskTimeoutMinutes + 30, 90); // Add 30 min buffer, minimum 90 min

  // Create clean payload excluding JWT reserved claims
  const cleanPayload = {
    id: payload.id,
    churchId: payload.churchId,
    email: payload.email,
    role: payload.role,
    firstName: payload.firstName,
    lastName: payload.lastName,
  };

  return jwt.sign(cleanPayload, JWT_SECRET, { expiresIn: `${extendedMinutes}m` });
};

export const verifyToken = (token: string): ChurchUserPayload | null => {
  try {
    return jwt.verify(token, JWT_SECRET) as ChurchUserPayload;
  } catch (error) {
    return null;
  }
};

// Password utilities
export const hashPassword = async (password: string): Promise<string> => {
  return await bcrypt.hash(password, SALT_ROUNDS);
};

export const verifyPassword = async (password: string, hash: string): Promise<boolean> => {
  return await bcrypt.compare(password, hash);
};

// Authentication middleware with kiosk session extension
export const authenticateToken = async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

  if (!token) {
    return res.status(401).json({ error: 'Access token required' });
  }

  let user = verifyToken(token);

  // If token is expired but kiosk session is active, extend the session
  if (!user) {
    try {
      // Try to decode the token without verification to get user info
      const decoded = jwt.decode(token) as ChurchUserPayload | null;
      if (decoded?.churchId) {
        const { churchStorage } = await import('./church-storage.js');
        const church = await churchStorage.getChurchById(decoded.churchId);

        if (church && church.kioskModeEnabled && church.kioskActiveEventId && church.kioskSessionStartTime) {
          const sessionStart = new Date(church.kioskSessionStartTime);
          const now = new Date();
          const sessionTimeout = (church.kioskSessionTimeout || 60) * 60 * 1000;
          const sessionEnd = new Date(sessionStart.getTime() + sessionTimeout);

          // If kiosk session is still active, allow expired token to continue
          if (now < sessionEnd) {
            const extendedToken = generateKioskToken(decoded, church.kioskSessionTimeout || 60);
            res.setHeader('X-Extended-Token', extendedToken);
            req.user = decoded;
            req.churchId = decoded.churchId;
            return next();
          }
        }
      }
    } catch (error) {
      console.error('Kiosk session extension failed:', error);
    }

    return res.status(403).json({ error: 'Invalid or expired token' });
  }

  // Check if church is suspended
  try {
    const { churchStorage } = await import('./church-storage.js');
    const church = await churchStorage.getChurchById(user.churchId);

    const subscriptionTier = church?.subscriptionTier || (church as any)?.subscription_tier;
    if (church && subscriptionTier === 'suspended') {
      return res.status(403).json({ 
        error: 'Church account is suspended. Please contact support for assistance.',
        suspended: true 
      });
    }
  } catch (error) {
    console.error('Church suspension check failed:', error);
    // Continue on error to not break existing functionality
  }

  req.user = user;
  req.churchId = user.churchId;
  next();
};

// Role-based authorization middleware
export const requireRole = (allowedRoles: string[]) => {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    if (!allowedRoles.includes(req.user.role)) {
      return res.status(403).json({ error: 'Insufficient permissions' });
    }

    next();
  };
};

// Church context middleware - ensures all requests are scoped to user's church
export const ensureChurchContext = (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  if (!req.churchId) {
    return res.status(400).json({ error: 'Church context required' });
  }
  next();
};

// Subscription tier enforcement
export const requireSubscriptionTier = (requiredTiers: string[]) => {
  return async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    // This would check the church's subscription tier from database
    // For now, we'll implement basic logic
    // TODO: Implement actual subscription checking
    next();
  };
};

// Generate a secure subdomain from church name
export const generateSubdomain = (churchName: string): string => {
  return churchName
    .toLowerCase()
    .replace(/[^a-z0-9\s-]/g, '') // Remove special characters
    .replace(/\s+/g, '-') // Replace spaces with hyphens
    .replace(/-+/g, '-') // Replace multiple hyphens with single
    .replace(/^-|-$/g, '') // Remove leading/trailing hyphens
    .substring(0, 50); // Limit length
};

server/church-routes.ts.content:
import { Router } from 'express';
import { z } from 'zod';
import { churchStorage } from './church-storage.js';
import { 
  generateToken, 
  hashPassword, 
  verifyPassword, 
  generateSubdomain,
  authenticateToken,
  requireRole,
  ensureChurchContext,
  type AuthenticatedRequest 
} from './auth.js';
import { eq } from "drizzle-orm";
import { db } from "./db.js";
import { insertChurchSchema, insertChurchUserSchema, kioskSettingsSchema, events } from '../shared/schema.js';
import { generateKioskToken, type ChurchUserPayload } from './auth.js';

const router = Router();

// Church registration schema
const churchRegistrationSchema = z.object({
  churchName: z.string().min(1, "Church name is required"),
  adminFirstName: z.string().min(1, "First name is required"),
  adminLastName: z.string().min(1, "Last name is required"),
  adminEmail: z.string().email("Invalid email format"),
  password: z.string().min(8, "Password must be at least 8 characters"),
  subdomain: z.string().min(3, "Subdomain must be at least 3 characters").regex(/^[a-z0-9-]+$/, "Subdomain can only contain lowercase letters, numbers, and hyphens").optional(),
});

// Church login schema
const churchLoginSchema = z.object({
  email: z.string().email("Invalid email format"),
  password: z.string().min(1, "Password is required"),
});



// POST /api/churches/register - Register new church with admin user
router.post('/register', async (req, res) => {
  try {
    const registrationData = churchRegistrationSchema.parse(req.body);

    // Check if email already exists
    const existingUser = await churchStorage.getChurchUserByEmail(registrationData.adminEmail);
    if (existingUser) {
      return res.status(400).json({ error: 'Email already registered' });
    }

    // Generate subdomain if not provided
    let subdomain = registrationData.subdomain || generateSubdomain(registrationData.churchName);

    // Ensure subdomain is unique
    let counter = 1;
    let originalSubdomain = subdomain;
    while (!(await churchStorage.isSubdomainAvailable(subdomain))) {
      subdomain = `${originalSubdomain}-${counter}`;
      counter++;
    }

    // Create church
    const church = await churchStorage.createChurch({
      name: registrationData.churchName,
      subdomain,
      subscriptionTier: 'trial',
      maxMembers: 999999, // Unlimited during trial
      kioskModeEnabled: false,
      kioskSessionTimeout: 60,
    });

    // Hash password and create admin user
    const passwordHash = await hashPassword(registrationData.password);
    const adminUser = await churchStorage.createChurchUser({
      churchId: church.id,
      email: registrationData.adminEmail,
      passwordHash,
      firstName: registrationData.adminFirstName,
      lastName: registrationData.adminLastName,
      role: 'admin',
      isActive: true,
    });

    // Generate JWT token
    const token = generateToken({
      id: adminUser.id,
      churchId: church.id,
      email: adminUser.email,
      role: adminUser.role,
      firstName: adminUser.firstName,
      lastName: adminUser.lastName,
    });

    res.status(201).json({
      success: true,
      church: {
        id: church.id,
        name: church.name,
        subdomain: church.subdomain,
        subscriptionTier: church.subscriptionTier,
        trialEndDate: church.trialEndDate,
      },
      user: {
        id: adminUser.id,
        email: adminUser.email,
        firstName: adminUser.firstName,
        lastName: adminUser.lastName,
        role: adminUser.role,
      },
      token,
    });
  } catch (error) {
    console.error('Church registration error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Registration failed' });
  }
});

// POST /api/churches/login - Church user login
router.post('/login', async (req, res) => {
  try {
    const loginData = churchLoginSchema.parse(req.body);

    // Find user by email
    const user = await churchStorage.getChurchUserByEmail(loginData.email);
    if (!user) {
      return res.status(401).json({ error: 'Invalid email or password' });
    }

    // Verify password
    const isValidPassword = await verifyPassword(loginData.password, user.passwordHash);
    if (!isValidPassword) {
      return res.status(401).json({ error: 'Invalid email or password' });
    }

    // Check if user is active
    if (!user.isActive) {
      return res.status(401).json({ error: 'Account is deactivated' });
    }

    // Get church details
    const church = await churchStorage.getChurchById(user.churchId);
    if (!church) {
      return res.status(500).json({ error: 'Church not found' });
    }

    // Check if church is suspended (handle both camelCase and snake_case)
    const subscriptionTier = church.subscriptionTier || (church as any).subscription_tier;
    if (subscriptionTier === 'suspended') {
      return res.status(403).json({ 
        error: 'Church account is suspended. Please contact support for assistance.',
        suspended: true 
      });
    }

    // Update last login
    await churchStorage.updateLastLogin(user.id);

    // Generate JWT token
    const token = generateToken({
      id: user.id,
      churchId: user.churchId,
      email: user.email,
      role: user.role,
      firstName: user.firstName,
      lastName: user.lastName,
    });

    // Check trial status
    const trialDaysRemaining = await churchStorage.getTrialDaysRemaining(church.id);
    const isTrialActive = await churchStorage.isTrialActive(church.id);

    res.json({
      success: true,
      church: {
        id: church.id,
        name: church.name,
        subdomain: church.subdomain,
        subscriptionTier: church.subscriptionTier,
        trialEndDate: church.trialEndDate,
        logoUrl: church.logoUrl,
        brandColor: church.brandColor,
        isTrialActive,
        trialDaysRemaining,
      },
      user: {
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        role: user.role,
      },
      token,
    });
  } catch (error) {
    console.error('Login error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Login failed' });
  }
});

// GET /api/churches/me - Get current church and user info
router.get('/me', authenticateToken, async (req: AuthenticatedRequest, res) => {
  try {
    const church = await churchStorage.getChurchById(req.churchId!);
    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    const trialDaysRemaining = await churchStorage.getTrialDaysRemaining(church.id);
    const isTrialActive = await churchStorage.isTrialActive(church.id);
    const memberCount = await churchStorage.getChurchMemberCount(church.id);

    res.json({
      church: {
        id: church.id,
        name: church.name,
        subdomain: church.subdomain,
        subscriptionTier: church.subscriptionTier,
        trialEndDate: church.trialEndDate,
        logoUrl: church.logoUrl,
        brandColor: church.brandColor,
        maxMembers: church.maxMembers,
        isTrialActive,
        trialDaysRemaining,
        memberCount,
      },
      user: req.user,
    });
  } catch (error) {
    console.error('Get church info error:', error);
    res.status(500).json({ error: 'Failed to get church information' });
  }
});

// PUT /api/churches/settings - Update church settings
router.put('/settings', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const updateSchema = z.object({
      name: z.string().min(1, "Church name is required").optional(),
      logoUrl: z.string().url("Invalid logo URL").optional(),
      brandColor: z.string().regex(/^#[0-9A-F]{6}$/i, "Brand color must be a valid hex color").optional(),
    });

    const updates = updateSchema.parse(req.body);
    const church = await churchStorage.updateChurch(req.churchId!, updates);

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    res.json({ success: true, church });
  } catch (error) {
    console.error('Update church settings error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Failed to update church settings' });
  }
});

// GET /api/churches/kiosk-settings - Get current kiosk settings
router.get('/kiosk-settings', authenticateToken, async (req: AuthenticatedRequest, res) => {
  try {
    const church = await churchStorage.getChurchById(req.churchId!);

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    // Calculate remaining time if session is active
    let timeRemaining = null;
    let isSessionActive = false;

    if (church.kioskSessionStartTime) {
      const sessionStart = new Date(church.kioskSessionStartTime);
      const sessionTimeout = church.kioskSessionTimeout || 60;
      const sessionEnd = new Date(sessionStart.getTime() + sessionTimeout * 60 * 1000);
      const now = new Date();

      if (now < sessionEnd) {
        timeRemaining = Math.max(0, Math.floor((sessionEnd.getTime() - now.getTime()) / 1000));
        isSessionActive = true;
      }
    }

    // Get all active events available for kiosk mode
    let availableEvents = [];
    try {
      const allEvents = await db.select().from(events).where(eq(events.churchId, req.churchId!));
      availableEvents = allEvents.filter(event => event.isActive).map(event => ({
        id: event.id,
        name: event.name,
        eventType: event.eventType,
        location: event.location
      }));
    } catch (e) {
      console.error('Error fetching active events:', e);
    }

    res.json({
      kioskModeEnabled: church.kioskModeEnabled || false,
      kioskSessionTimeout: church.kioskSessionTimeout || 60,
      activeSession: isSessionActive ? {
        timeRemaining: timeRemaining,
        isActive: true,
        availableEvents: availableEvents
      } : null
    });
  } catch (error) {
    console.error('Get kiosk settings error:', error);
    res.status(500).json({ error: 'Failed to get kiosk settings' });
  }
});

// POST /api/churches/kiosk-session/start - Start a kiosk session for all active events
router.post('/kiosk-session/start', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    // No validation needed - kiosk mode applies to all active events
    const church = await churchStorage.updateChurch(req.churchId!, {
      kioskSessionStartTime: new Date(),
    });

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    // Get all active events for display
    let availableEvents = [];
    try {
      const allEvents = await db.select().from(events).where(eq(events.churchId, req.churchId!));
      availableEvents = allEvents.filter(event => event.isActive);
    } catch (e) {
      console.error('Error fetching active events:', e);
    }

    // Generate extended token for kiosk session persistence
    const userPayload: ChurchUserPayload = {
      id: req.user!.id,
      churchId: req.user!.churchId,
      email: req.user!.email,
      role: req.user!.role,
      firstName: req.user!.firstName,
      lastName: req.user!.lastName,
    };
    const extendedToken = generateKioskToken(userPayload, church.kioskSessionTimeout || 60);

    res.json({ 
      success: true, 
      message: 'Kiosk session started successfully for all active events',
      session: {
        startTime: church.kioskSessionStartTime,
        timeoutMinutes: church.kioskSessionTimeout,
        availableEvents: availableEvents.length
      },
      extendedToken // Send back extended token for session persistence
    });
  } catch (error) {
    console.error('Start kiosk session error:', error);
    res.status(500).json({ error: 'Failed to start kiosk session' });
  }
});

// POST /api/churches/kiosk-session/extend - Extend current kiosk session
router.post('/kiosk-session/extend', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    // Reset session start time to extend the session
    const church = await churchStorage.updateChurch(req.churchId!, {
      kioskSessionStartTime: new Date(),
    });

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    // Generate new extended token for session extension
    const userPayload: ChurchUserPayload = {
      id: req.user!.id,
      churchId: req.user!.churchId,
      email: req.user!.email,
      role: req.user!.role,
      firstName: req.user!.firstName,
      lastName: req.user!.lastName,
    };
    const extendedToken = generateKioskToken(userPayload, church.kioskSessionTimeout || 60);

    res.json({ 
      success: true, 
      message: 'Kiosk session extended successfully',
      newStartTime: church.kioskSessionStartTime,
      extendedToken // Send back new extended token
    });
  } catch (error) {
    console.error('Extend kiosk session error:', error);
    res.status(500).json({ error: 'Failed to extend kiosk session' });
  }
});

// POST /api/churches/kiosk-session/end - End current kiosk session
router.post('/kiosk-session/end', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const church = await churchStorage.updateChurch(req.churchId!, {
      kioskSessionStartTime: null,
    });

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    res.json({ 
      success: true, 
      message: 'Kiosk session ended successfully'
    });
  } catch (error) {
    console.error('End kiosk session error:', error);
    res.status(500).json({ error: 'Failed to end kiosk session' });
  }
});

// PATCH /api/churches/kiosk-settings - Update kiosk mode settings
router.patch('/kiosk-settings', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const settings = kioskSettingsSchema.parse(req.body);

    const church = await churchStorage.updateChurch(req.churchId!, {
      kioskModeEnabled: settings.kioskModeEnabled,
      kioskSessionTimeout: settings.kioskSessionTimeout,
    });

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    res.json({ 
      success: true, 
      message: 'Kiosk settings updated successfully',
      settings: {
        kioskModeEnabled: church.kioskModeEnabled,
        kioskSessionTimeout: church.kioskSessionTimeout,
      }
    });
  } catch (error) {
    console.error('Update kiosk settings error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Failed to update kiosk settings' });
  }
});

// GET /api/churches/features - Get available features for current subscription
router.get('/features', authenticateToken, async (req: AuthenticatedRequest, res) => {
  try {
    const churchId = req.churchId!;

    const features = {
      biometric_checkin: await churchStorage.hasFeatureAccess(churchId, 'biometric_checkin'),
      family_checkin: await churchStorage.hasFeatureAccess(churchId, 'family_checkin'),
      visitor_management: await churchStorage.hasFeatureAccess(churchId, 'visitor_management'),
      history_tracking: await churchStorage.hasFeatureAccess(churchId, 'history_tracking'),
      follow_up_queue: await churchStorage.hasFeatureAccess(churchId, 'follow_up_queue'),
      basic_reports: await churchStorage.hasFeatureAccess(churchId, 'basic_reports'),
      full_analytics: await churchStorage.hasFeatureAccess(churchId, 'full_analytics'),
      email_notifications: await churchStorage.hasFeatureAccess(churchId, 'email_notifications'),
      sms_notifications: await churchStorage.hasFeatureAccess(churchId, 'sms_notifications'),
      bulk_upload: await churchStorage.hasFeatureAccess(churchId, 'bulk_upload'),
      advanced_roles: await churchStorage.hasFeatureAccess(churchId, 'advanced_roles'),
      multi_location: await churchStorage.hasFeatureAccess(churchId, 'multi_location'),
      api_access: await churchStorage.hasFeatureAccess(churchId, 'api_access'),
      custom_branding: await churchStorage.hasFeatureAccess(churchId, 'custom_branding'),
    };

    res.json({ features });
  } catch (error) {
    console.error('Get features error:', error);
    res.status(500).json({ error: 'Failed to get feature access' });
  }
});

// GET /api/churches/usage - Get current usage statistics
router.get('/usage', authenticateToken, async (req: AuthenticatedRequest, res) => {
  try {
    const churchId = req.churchId!;
    const church = await churchStorage.getChurchById(churchId);

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    const memberCount = await churchStorage.getChurchMemberCount(churchId);
    const memberLimit = church.maxMembers;
    const memberUsagePercent = Math.round((memberCount / memberLimit) * 100);

    res.json({
      usage: {
        members: {
          current: memberCount,
          limit: memberLimit,
          percentage: memberUsagePercent,
        },
        subscriptionTier: church.subscriptionTier,
        trialDaysRemaining: await churchStorage.getTrialDaysRemaining(churchId),
      },
    });
  } catch (error) {
    console.error('Get usage error:', error);
    res.status(500).json({ error: 'Failed to get usage statistics' });
  }
});

// POST /api/churches/check-subdomain - Check if subdomain is available
router.post('/check-subdomain', async (req, res) => {
  try {
    const { subdomain } = z.object({
      subdomain: z.string().min(3, "Subdomain must be at least 3 characters").regex(/^[a-z0-9-]+$/, "Subdomain can only contain lowercase letters, numbers, and hyphens"),
    }).parse(req.body);

    const isAvailable = await churchStorage.isSubdomainAvailable(subdomain);

    res.json({ 
      available: isAvailable,
      subdomain,
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Invalid subdomain format', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Failed to check subdomain' });
  }
});

export default router;

server/church-storage.ts.content:
import { eq, and, sql } from 'drizzle-orm';
import { db } from './db.js';
import { 
  churches, 
  churchUsers, 
  subscriptions, 
  superAdmins,
  members,
  attendanceRecords,
  followUpRecords,
  visitors,
  type Church,
  type InsertChurch,
  type ChurchUser,
  type InsertChurchUser,
  type SuperAdmin,
  type InsertSuperAdmin,
  type Subscription,
  type InsertSubscription
} from '../shared/schema.js';

export class ChurchStorage {

  // Church management
  async createChurch(churchData: InsertChurch): Promise<Church> {
    const [church] = await db.insert(churches).values(churchData).returning();
    return church;
  }

  async getChurchById(id: string): Promise<Church | null> {
    const [church] = await db.select().from(churches).where(eq(churches.id, id));
    return church || null;
  }

  async getChurchBySubdomain(subdomain: string): Promise<Church | null> {
    const [church] = await db.select().from(churches).where(eq(churches.subdomain, subdomain));
    return church || null;
  }

  async updateChurch(id: string, updates: Partial<InsertChurch>): Promise<Church | null> {
    const [church] = await db
      .update(churches)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(churches.id, id))
      .returning();
    return church || null;
  }

  async isSubdomainAvailable(subdomain: string): Promise<boolean> {
    const [existing] = await db.select().from(churches).where(eq(churches.subdomain, subdomain));
    return !existing;
  }

  // Church user management
  async createChurchUser(userData: InsertChurchUser): Promise<ChurchUser> {
    const [user] = await db.insert(churchUsers).values(userData).returning();
    return user;
  }

  async getChurchUserByEmail(email: string): Promise<ChurchUser | null> {
    const [user] = await db.select().from(churchUsers).where(eq(churchUsers.email, email));
    return user || null;
  }

  async getChurchUsers(churchId: string): Promise<ChurchUser[]> {
    return await db.select().from(churchUsers).where(eq(churchUsers.churchId, churchId));
  }

  async updateChurchUser(id: string, updates: Partial<InsertChurchUser>): Promise<ChurchUser | null> {
    const [user] = await db
      .update(churchUsers)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(churchUsers.id, id))
      .returning();
    return user || null;
  }

  async updateLastLogin(userId: string): Promise<void> {
    await db
      .update(churchUsers)
      .set({ lastLoginAt: new Date() })
      .where(eq(churchUsers.id, userId));
  }

  // Super Admin management
  async createSuperAdmin(adminData: InsertSuperAdmin): Promise<SuperAdmin> {
    const [admin] = await db.insert(superAdmins).values(adminData).returning();
    return admin;
  }

  async getSuperAdminByEmail(email: string): Promise<SuperAdmin | null> {
    const [admin] = await db.select().from(superAdmins).where(eq(superAdmins.email, email));
    return admin || null;
  }

  async getSuperAdminById(id: string): Promise<SuperAdmin | null> {
    const [admin] = await db.select().from(superAdmins).where(eq(superAdmins.id, id));
    return admin || null;
  }

  async getAllSuperAdmins(): Promise<Array<{
    id: string;
    email: string;
    firstName: string;
    lastName: string;
    role: string;
    isActive: boolean;
    lastLoginAt: string | null;
    createdAt: string;
  }>> {
    const result = await db
      .select({
        id: superAdmins.id,
        email: superAdmins.email,
        firstName: superAdmins.firstName,
        lastName: superAdmins.lastName,
        role: superAdmins.role,
        isActive: superAdmins.isActive,
        lastLoginAt: superAdmins.lastLoginAt,
        createdAt: superAdmins.createdAt
      })
      .from(superAdmins)
      .orderBy(superAdmins.createdAt);

    return result.map(admin => ({
      ...admin,
      isActive: admin.isActive ?? false,
      lastLoginAt: admin.lastLoginAt ? admin.lastLoginAt.toISOString() : null,
      createdAt: admin.createdAt.toISOString()
    }));
  }

  async updateSuperAdminLastLogin(adminId: string): Promise<void> {
    await db
      .update(superAdmins)
      .set({ lastLoginAt: new Date() })
      .where(eq(superAdmins.id, adminId));
  }

  // Platform management methods
  async getAllChurches(): Promise<Church[]> {
    return await db.select().from(churches).orderBy(churches.createdAt);
  }

  async getChurchStats(churchId: string): Promise<{
    totalMembers: number;
    activeMembers: number;
    totalAttendance: number;
    subscriptionTier: string;
  }> {
    const church = await this.getChurchById(churchId);
    if (!church) {
      throw new Error('Church not found');
    }

    const [memberStats] = await db
      .select({
        totalMembers: sql<number>`count(*)`,
        activeMembers: sql<number>`count(*) filter (where ${members.isCurrentMember} = true)`,
      })
      .from(members)
      .where(eq(members.churchId, churchId));

    const [attendanceStats] = await db
      .select({
        totalAttendance: sql<number>`count(*)`,
      })
      .from(attendanceRecords)
      .where(eq(attendanceRecords.churchId, churchId));

    return {
      totalMembers: memberStats.totalMembers,
      activeMembers: memberStats.activeMembers,
      totalAttendance: attendanceStats.totalAttendance,
      subscriptionTier: church.subscriptionTier,
    };
  }

  async getPlatformStats(): Promise<{
    totalChurches: number;
    totalMembers: number;
    totalAttendance: number;
    activeChurches: number;
  }> {
    const [churchStats] = await db
      .select({
        totalChurches: sql<number>`count(*)`,
      })
      .from(churches);

    const [memberStats] = await db
      .select({
        totalMembers: sql<number>`count(*)`,
      })
      .from(members);

    const [attendanceStats] = await db
      .select({
        totalAttendance: sql<number>`count(*)`,
      })
      .from(attendanceRecords);

    // Active churches = churches with activity in last 30 days
    const [activeStats] = await db
      .select({
        activeChurches: sql<number>`count(distinct ${attendanceRecords.churchId})`,
      })
      .from(attendanceRecords)
      .where(sql`${attendanceRecords.checkInTime} >= NOW() - INTERVAL '30 days'`);

    return {
      totalChurches: churchStats.totalChurches,
      totalMembers: memberStats.totalMembers,
      totalAttendance: attendanceStats.totalAttendance,
      activeChurches: activeStats.activeChurches,
    };
  }

  // Get church by ID (alias for getChurchById for consistency)
  async getChurch(id: string): Promise<Church | null> {
    return this.getChurchById(id);
  }

  // Update church branding
  async updateChurchBranding(churchId: string, brandingData: { logoUrl?: string; bannerUrl?: string; brandColor?: string }): Promise<Church | null> {
    // Convert empty strings to null for database storage
    const processedData: any = { updatedAt: new Date() };
    if (brandingData.logoUrl !== undefined) {
      processedData.logoUrl = brandingData.logoUrl === "" ? null : brandingData.logoUrl;
    }
    if (brandingData.bannerUrl !== undefined) {
      processedData.bannerUrl = brandingData.bannerUrl === "" ? null : brandingData.bannerUrl;
    }
    if (brandingData.brandColor !== undefined) {
      processedData.brandColor = brandingData.brandColor === "" ? null : brandingData.brandColor;
    }

    const [church] = await db
      .update(churches)
      .set(processedData)
      .where(eq(churches.id, churchId))
      .returning();
    return church || null;
  }

  async deleteChurchUser(id: string): Promise<boolean> {
    const result = await db.delete(churchUsers).where(eq(churchUsers.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  // Subscription management
  async createSubscription(subscriptionData: InsertSubscription): Promise<Subscription> {
    const [subscription] = await db.insert(subscriptions).values(subscriptionData).returning();
    return subscription;
  }

  async getChurchSubscription(churchId: string): Promise<Subscription | null> {
    const [subscription] = await db
      .select()
      .from(subscriptions)
      .where(eq(subscriptions.churchId, churchId));
    return subscription || null;
  }

  async updateSubscription(churchId: string, updates: Partial<InsertSubscription>): Promise<Subscription | null> {
    const [subscription] = await db
      .update(subscriptions)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(subscriptions.churchId, churchId))
      .returning();
    return subscription || null;
  }

  // Church statistics and limits
  async getChurchMemberCount(churchId: string): Promise<number> {
    const [result] = await db
      .select({ count: sql<number>`count(*)` })
      .from(members)
      .where(eq(members.churchId, churchId));
    return result.count;
  }

  async canAddMember(churchId: string): Promise<{ allowed: boolean; reason?: string }> {
    const church = await this.getChurchById(churchId);
    if (!church) {
      return { allowed: false, reason: 'Church not found' };
    }

    const memberCount = await this.getChurchMemberCount(churchId);

    // Check subscription limits
    if (church.maxMembers && memberCount >= church.maxMembers) {
      return { 
        allowed: false, 
        reason: `Member limit reached (${church.maxMembers}). Upgrade your subscription to add more members.` 
      };
    }

    return { allowed: true };
  }

  // Trial management
  async isTrialActive(churchId: string): Promise<boolean> {
    const church = await this.getChurchById(churchId);
    if (!church) return false;

    if (church.subscriptionTier !== 'trial') return false;

    const now = new Date();
    return church.trialEndDate ? now < church.trialEndDate : false;
  }

  async getTrialDaysRemaining(churchId: string): Promise<number> {
    const church = await this.getChurchById(churchId);
    if (!church || church.subscriptionTier !== 'trial' || !church.trialEndDate) {
      return 0;
    }

    const now = new Date();
    const diffTime = church.trialEndDate.getTime() - now.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  }

  // Feature gating based on subscription tier
  async hasFeatureAccess(churchId: string, feature: string): Promise<boolean> {
    const church = await this.getChurchById(churchId);
    if (!church) return false;

    const { subscriptionTier } = church;
    const isTrialActive = await this.isTrialActive(churchId);

    // During trial, all features are available
    if (isTrialActive) return true;

    // Feature matrix based on subscription tiers
    const featureMatrix: Record<string, string[]> = {
      // Basic features available to all paid tiers
      'basic_checkin': ['starter', 'growth', 'enterprise'],
      'member_management': ['starter', 'growth', 'enterprise'],
      'basic_reports': ['starter', 'growth', 'enterprise'],

      // Growth tier features
      'biometric_checkin': ['growth', 'enterprise'],
      'family_checkin': ['growth', 'enterprise'],
      'visitor_management': ['growth', 'enterprise'],
      'history_tracking': ['growth', 'enterprise'],
      'follow_up_queue': ['growth', 'enterprise'],
      'email_notifications': ['growth', 'enterprise'],

      // Enterprise-only features
      'full_analytics': ['enterprise'],
      'sms_notifications': ['enterprise'],
      'bulk_upload': ['enterprise'],
      'advanced_roles': ['enterprise'],
      'multi_location': ['enterprise'],
      'api_access': ['enterprise'],
      'custom_branding': ['enterprise'],
    };

    const allowedTiers = featureMatrix[feature] || [];
    return allowedTiers.includes(subscriptionTier);
  }

  // Enhanced feature access with usage limits
  async checkFeatureLimit(churchId: string, feature: string, currentUsage: number): Promise<{
    allowed: boolean;
    reason?: string;
    limit?: number;
  }> {
    const hasAccess = await this.hasFeatureAccess(churchId, feature);
    if (!hasAccess) {
      return { 
        allowed: false, 
        reason: 'Feature not available in current subscription tier' 
      };
    }

    const church = await this.getChurchById(churchId);
    if (!church) {
      return { allowed: false, reason: 'Church not found' };
    }

    // Define usage limits per tier
    const usageLimits: Record<string, Record<string, number>> = {
      starter: {
        members: 100,
        monthly_reports: 5,
        email_notifications: 100,
      },
      growth: {
        members: 999999,
        monthly_reports: 50,
        email_notifications: 1000,
        sms_notifications: 0, // Not available
      },
      enterprise: {
        members: 999999,
        monthly_reports: 999999,
        email_notifications: 999999,
        sms_notifications: 999999,
      },
    };

    const tierLimits = usageLimits[church.subscriptionTier] || {};
    const limit = tierLimits[feature];

    if (limit !== undefined && currentUsage >= limit) {
      return {
        allowed: false,
        reason: `Usage limit reached (${limit}). Upgrade your subscription for higher limits.`,
        limit,
      };
    }

    return { allowed: true, limit };
  }

  // Church onboarding and setup
  async completeChurchSetup(churchId: string, setupData: {
    logoUrl?: string;
    brandColor?: string;
    subdomain?: string;
  }): Promise<Church | null> {
    return await this.updateChurch(churchId, setupData);
  }

  // Cleanup and data management
  async deleteChurchData(churchId: string): Promise<void> {
    // Delete in order to respect foreign key constraints
    await db.delete(attendanceRecords).where(eq(attendanceRecords.churchId, churchId));
    await db.delete(followUpRecords).where(eq(followUpRecords.churchId, churchId));
    await db.delete(visitors).where(eq(visitors.churchId, churchId));
    await db.delete(members).where(eq(members.churchId, churchId));
    await db.delete(subscriptions).where(eq(subscriptions.churchId, churchId));
    await db.delete(churchUsers).where(eq(churchUsers.churchId, churchId));
    await db.delete(churches).where(eq(churches.id, churchId));
  }
}

export const churchStorage = new ChurchStorage();

