import Stripe from 'stripe';
import { churchStorage } from './church-storage.js';

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY || 'sk_test_dummy', {
  apiVersion: '2024-12-18.acacia',
});

// Subscription tier configuration
export const SUBSCRIPTION_PLANS = {
  starter: {
    priceId: process.env.STRIPE_STARTER_PRICE_ID || 'price_starter',
    maxMembers: 100,
    features: ['basic_checkin', 'member_management', 'basic_reports'],
    monthlyPrice: 1900, // $19.00 in cents
  },
  growth: {
    priceId: process.env.STRIPE_GROWTH_PRICE_ID || 'price_growth',
    maxMembers: 999999,
    features: [
      'basic_checkin', 'member_management', 'basic_reports',
      'biometric_checkin', 'family_checkin', 'visitor_management',
      'history_tracking', 'follow_up_queue', 'email_notifications'
    ],
    monthlyPrice: 4900, // $49.00 in cents
  },
  enterprise: {
    priceId: process.env.STRIPE_ENTERPRISE_PRICE_ID || 'price_enterprise',
    maxMembers: 999999,
    features: [
      'basic_checkin', 'member_management', 'basic_reports',
      'biometric_checkin', 'family_checkin', 'visitor_management',
      'history_tracking', 'follow_up_queue', 'email_notifications',
      'full_analytics', 'sms_notifications', 'bulk_upload',
      'advanced_roles', 'multi_location', 'api_access', 'custom_branding'
    ],
    monthlyPrice: 9900, // $99.00 in cents
  },
};

export class StripeService {

  // Create a customer for the church
  async createCustomer(churchId: string, email: string, name: string): Promise<Stripe.Customer> {
    const customer = await stripe.customers.create({
      email,
      name,
      metadata: {
        churchId,
      },
    });

    return customer;
  }

  // Create a checkout session for subscription
  async createCheckoutSession(
    churchId: string,
    planId: keyof typeof SUBSCRIPTION_PLANS,
    successUrl: string,
    cancelUrl: string,
    customerId?: string
  ): Promise<Stripe.Checkout.Session> {
    const plan = SUBSCRIPTION_PLANS[planId];
    if (!plan) {
      throw new Error(`Invalid plan: ${planId}`);
    }

    const church = await churchStorage.getChurchById(churchId);
    if (!church) {
      throw new Error('Church not found');
    }

    const sessionParams: Stripe.Checkout.SessionCreateParams = {
      mode: 'subscription',
      payment_method_types: ['card'],
      line_items: [
        {
          price: plan.priceId,
          quantity: 1,
        },
      ],
      success_url: successUrl,
      cancel_url: cancelUrl,
      metadata: {
        churchId,
        planId,
      },
      client_reference_id: churchId,
    };

    if (customerId) {
      sessionParams.customer = customerId;
    } else {
      sessionParams.customer_email = church.name; // Will need to get admin email
    }

    // Add trial period if church is currently on trial
    const isTrialActive = await churchStorage.isTrialActive(churchId);
    if (isTrialActive) {
      sessionParams.subscription_data = {
        trial_period_days: 0, // No additional trial since they already had one
      };
    }

    const session = await stripe.checkout.sessions.create(sessionParams);
    return session;
  }

  // Handle successful subscription creation
  async handleSubscriptionCreated(subscription: Stripe.Subscription): Promise<void> {
    const churchId = subscription.metadata?.churchId;
    if (!churchId) {
      throw new Error('No church ID in subscription metadata');
    }

    const planId = subscription.metadata?.planId as keyof typeof SUBSCRIPTION_PLANS;
    if (!planId || !SUBSCRIPTION_PLANS[planId]) {
      throw new Error('Invalid plan ID in subscription metadata');
    }

    const plan = SUBSCRIPTION_PLANS[planId];

    // Update church subscription tier and limits
    await churchStorage.updateChurch(churchId, {
      subscriptionTier: planId,
      maxMembers: plan.maxMembers,
      subscriptionStartDate: new Date(subscription.current_period_start * 1000),
    });

    // Create or update subscription record
    const existingSubscription = await churchStorage.getChurchSubscription(churchId);

    if (existingSubscription) {
      await churchStorage.updateSubscription(churchId, {
        stripeSubscriptionId: subscription.id,
        status: subscription.status,
        planId,
        currentPeriodStart: new Date(subscription.current_period_start * 1000),
        currentPeriodEnd: new Date(subscription.current_period_end * 1000),
        cancelAtPeriodEnd: subscription.cancel_at_period_end,
      });
    } else {
      await churchStorage.createSubscription({
        churchId,
        stripeSubscriptionId: subscription.id,
        status: subscription.status,
        planId,
        currentPeriodStart: new Date(subscription.current_period_start * 1000),
        currentPeriodEnd: new Date(subscription.current_period_end * 1000),
        cancelAtPeriodEnd: subscription.cancel_at_period_end,
      });
    }
  }

  // Handle subscription updates (upgrades/downgrades)
  async handleSubscriptionUpdated(subscription: Stripe.Subscription): Promise<void> {
    const churchId = subscription.metadata?.churchId;
    if (!churchId) return;

    const planId = subscription.metadata?.planId as keyof typeof SUBSCRIPTION_PLANS;
    if (!planId || !SUBSCRIPTION_PLANS[planId]) return;

    const plan = SUBSCRIPTION_PLANS[planId];

    // Update church limits
    await churchStorage.updateChurch(churchId, {
      subscriptionTier: planId,
      maxMembers: plan.maxMembers,
    });

    // Update subscription record
    await churchStorage.updateSubscription(churchId, {
      status: subscription.status,
      planId,
      currentPeriodStart: new Date(subscription.current_period_start * 1000),
      currentPeriodEnd: new Date(subscription.current_period_end * 1000),
      cancelAtPeriodEnd: subscription.cancel_at_period_end,
    });
  }

  // Handle subscription cancellation
  async handleSubscriptionDeleted(subscription: Stripe.Subscription): Promise<void> {
    const churchId = subscription.metadata?.churchId;
    if (!churchId) return;

    // Downgrade to trial or basic tier
    await churchStorage.updateChurch(churchId, {
      subscriptionTier: 'trial',
      maxMembers: 100, // Basic limit
    });

    // Update subscription status
    await churchStorage.updateSubscription(churchId, {
      status: 'canceled',
    });
  }

  // Create a portal session for subscription management
  async createPortalSession(churchId: string, returnUrl: string): Promise<Stripe.BillingPortal.Session> {
    const subscription = await churchStorage.getChurchSubscription(churchId);
    if (!subscription?.stripeSubscriptionId) {
      throw new Error('No active subscription found');
    }

    const stripeSubscription = await stripe.subscriptions.retrieve(subscription.stripeSubscriptionId);
    if (!stripeSubscription.customer) {
      throw new Error('No customer associated with subscription');
    }

    const customerId = typeof stripeSubscription.customer === 'string' 
      ? stripeSubscription.customer 
      : stripeSubscription.customer.id;

    const session = await stripe.billingPortal.sessions.create({
      customer: customerId,
      return_url: returnUrl,
    });

    return session;
  }

  // Verify webhook signature
  verifyWebhookSignature(payload: string, signature: string): Stripe.Event {
    const endpointSecret = process.env.STRIPE_WEBHOOK_SECRET;
    if (!endpointSecret) {
      throw new Error('Stripe webhook secret not configured');
    }

    return stripe.webhooks.constructEvent(payload, signature, endpointSecret);
  }

  // Get subscription details
  async getSubscriptionDetails(subscriptionId: string): Promise<Stripe.Subscription> {
    return await stripe.subscriptions.retrieve(subscriptionId);
  }

  // Cancel subscription at period end
  async cancelSubscription(subscriptionId: string): Promise<Stripe.Subscription> {
    return await stripe.subscriptions.update(subscriptionId, {
      cancel_at_period_end: true,
    });
  }

  // Reactivate subscription
  async reactivateSubscription(subscriptionId: string): Promise<Stripe.Subscription> {
    return await stripe.subscriptions.update(subscriptionId, {
      cancel_at_period_end: false,
    });
  }

  // Change subscription plan
  async changeSubscriptionPlan(
    subscriptionId: string, 
    newPlanId: keyof typeof SUBSCRIPTION_PLANS
  ): Promise<Stripe.Subscription> {
    const plan = SUBSCRIPTION_PLANS[newPlanId];
    if (!plan) {
      throw new Error(`Invalid plan: ${newPlanId}`);
    }

    const subscription = await stripe.subscriptions.retrieve(subscriptionId);

    return await stripe.subscriptions.update(subscriptionId, {
      items: [
        {
          id: subscription.items.data[0].id,
          price: plan.priceId,
        },
      ],
      metadata: {
        ...subscription.metadata,
        planId: newPlanId,
      },
    });
  }
}

export const stripeService = new StripeService();

churchconnect-saas/server/subscription-r.content:
import { Router } from 'express';
import express from 'express';
import { z } from 'zod';
import { stripeService, SUBSCRIPTION_PLANS } from './stripe-service.js';
import { churchStorage } from './church-storage.js';
import { 
  authenticateToken, 
  requireRole, 
  type AuthenticatedRequest 
} from './auth.js';

const router = Router();

// Create checkout session for subscription
const createCheckoutSchema = z.object({
  planId: z.enum(['starter', 'growth', 'enterprise']),
  successUrl: z.string().url(),
  cancelUrl: z.string().url(),
});

router.post('/checkout', authenticateToken, async (req: AuthenticatedRequest, res) => {
  try {
    const { planId, successUrl, cancelUrl } = createCheckoutSchema.parse(req.body);
    const churchId = req.churchId!;

    // Check if church already has an active subscription
    const existingSubscription = await churchStorage.getChurchSubscription(churchId);
    if (existingSubscription && existingSubscription.status === 'active') {
      return res.status(400).json({ 
        error: 'Church already has an active subscription. Use billing portal to make changes.' 
      });
    }

    const session = await stripeService.createCheckoutSession(
      churchId,
      planId,
      successUrl,
      cancelUrl
    );

    res.json({ 
      sessionId: session.id,
      url: session.url 
    });
  } catch (error) {
    console.error('Create checkout session error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Failed to create checkout session' });
  }
});

// Create billing portal session
const portalSchema = z.object({
  returnUrl: z.string().url(),
});

router.post('/portal', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const { returnUrl } = portalSchema.parse(req.body);
    const churchId = req.churchId!;

    const session = await stripeService.createPortalSession(churchId, returnUrl);

    res.json({ url: session.url });
  } catch (error) {
    console.error('Create portal session error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Failed to create billing portal session' });
  }
});

// Get subscription status and details
router.get('/status', authenticateToken, async (req: AuthenticatedRequest, res) => {
  try {
    const churchId = req.churchId!;
    const church = await churchStorage.getChurchById(churchId);
    const subscription = await churchStorage.getChurchSubscription(churchId);

    if (!church) {
      return res.status(404).json({ error: 'Church not found' });
    }

    const isTrialActive = await churchStorage.isTrialActive(churchId);
    const trialDaysRemaining = await churchStorage.getTrialDaysRemaining(churchId);
    const memberCount = await churchStorage.getChurchMemberCount(churchId);

    const planDetails = SUBSCRIPTION_PLANS[church.subscriptionTier as keyof typeof SUBSCRIPTION_PLANS] || null;

    res.json({
      church: {
        id: church.id,
        name: church.name,
        subscriptionTier: church.subscriptionTier,
        maxMembers: church.maxMembers,
        memberCount,
        memberUsagePercent: Math.round((memberCount / church.maxMembers) * 100),
        isTrialActive,
        trialDaysRemaining,
        trialEndDate: church.trialEndDate,
      },
      subscription: subscription ? {
        id: subscription.id,
        status: subscription.status,
        planId: subscription.planId,
        currentPeriodStart: subscription.currentPeriodStart,
        currentPeriodEnd: subscription.currentPeriodEnd,
        cancelAtPeriodEnd: subscription.cancelAtPeriodEnd,
      } : null,
      planDetails,
      availablePlans: Object.entries(SUBSCRIPTION_PLANS).map(([key, plan]) => ({
        id: key,
        name: key.charAt(0).toUpperCase() + key.slice(1),
        monthlyPrice: plan.monthlyPrice,
        maxMembers: plan.maxMembers,
        features: plan.features,
      })),
    });
  } catch (error) {
    console.error('Get subscription status error:', error);
    res.status(500).json({ error: 'Failed to get subscription status' });
  }
});

// Webhook endpoint for Stripe events (requires raw body parsing)
router.post('/webhook', express.raw({ type: 'application/json' }), async (req, res) => {
  try {
    const signature = req.headers['stripe-signature'] as string;
    const payload = req.body;

    if (!signature) {
      return res.status(400).json({ error: 'Missing stripe-signature header' });
    }

    const event = stripeService.verifyWebhookSignature(payload, signature);

    console.log('Stripe webhook received:', event.type);

    switch (event.type) {
      case 'customer.subscription.created':
        await stripeService.handleSubscriptionCreated(event.data.object);
        break;

      case 'customer.subscription.updated':
        await stripeService.handleSubscriptionUpdated(event.data.object);
        break;

      case 'customer.subscription.deleted':
        await stripeService.handleSubscriptionDeleted(event.data.object);
        break;

      case 'invoice.payment_succeeded':
        console.log('Payment succeeded for subscription:', event.data.object.subscription);
        break;

      case 'invoice.payment_failed':
        console.log('Payment failed for subscription:', event.data.object.subscription);
        // TODO: Send notification to church admin
        break;

      default:
        console.log('Unhandled webhook event type:', event.type);
    }

    res.json({ received: true });
  } catch (error) {
    console.error('Webhook error:', error);
    res.status(400).json({ error: 'Webhook processing failed' });
  }
});

// Manually trigger subscription upgrade/downgrade
const changePlanSchema = z.object({
  newPlanId: z.enum(['starter', 'growth', 'enterprise']),
});

router.post('/change-plan', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const { newPlanId } = changePlanSchema.parse(req.body);
    const churchId = req.churchId!;

    const subscription = await churchStorage.getChurchSubscription(churchId);
    if (!subscription?.stripeSubscriptionId) {
      return res.status(400).json({ error: 'No active subscription found' });
    }

    const updatedSubscription = await stripeService.changeSubscriptionPlan(
      subscription.stripeSubscriptionId,
      newPlanId
    );

    // Update local records
    await stripeService.handleSubscriptionUpdated(updatedSubscription);

    res.json({ 
      success: true, 
      message: `Subscription changed to ${newPlanId}`,
      subscription: updatedSubscription 
    });
  } catch (error) {
    console.error('Change plan error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        error: 'Validation error', 
        details: error.errors 
      });
    }

    res.status(500).json({ error: 'Failed to change subscription plan' });
  }
});

// Cancel subscription
router.post('/cancel', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const churchId = req.churchId!;

    const subscription = await churchStorage.getChurchSubscription(churchId);
    if (!subscription?.stripeSubscriptionId) {
      return res.status(400).json({ error: 'No active subscription found' });
    }

    const canceledSubscription = await stripeService.cancelSubscription(subscription.stripeSubscriptionId);

    // Update local records
    await churchStorage.updateSubscription(churchId, {
      cancelAtPeriodEnd: true,
    });

    res.json({ 
      success: true, 
      message: 'Subscription will be canceled at the end of the current period',
      subscription: canceledSubscription 
    });
  } catch (error) {
    console.error('Cancel subscription error:', error);
    res.status(500).json({ error: 'Failed to cancel subscription' });
  }
});

// Reactivate subscription
router.post('/reactivate', authenticateToken, requireRole(['admin']), async (req: AuthenticatedRequest, res) => {
  try {
    const churchId = req.churchId!;

    const subscription = await churchStorage.getChurchSubscription(churchId);
    if (!subscription?.stripeSubscriptionId) {
      return res.status(400).json({ error: 'No subscription found' });
    }

    const reactivatedSubscription = await stripeService.reactivateSubscription(subscription.stripeSubscriptionId);

    // Update local records
    await churchStorage.updateSubscription(churchId, {
      cancelAtPeriodEnd: false,
    });

    res.json({ 
      success: true, 
      message: 'Subscription reactivated',
      subscription: reactivatedSubscription 
    });
  } catch (error) {
    console.error('Reactivate subscription error:', error);
    res.status(500).json({ error: 'Failed to reactivate subscription' });
  }
});

export default router;

