  --church-warning: hsl(45, 93%, 47%);
  --church-error: hsl(0, 84%, 60%);
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(258, 90%, 66%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(258, 90%, 66%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-gradient-to-br from-slate-50 to-slate-100 text-foreground;
    font-family: 'Inter', sans-serif;
  }
}

/* Dashboard Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes countUp {
  from {
    opacity: 0;
    transform: scale(0.8);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes progressFill {
  from {
    width: 0%;
  }
  to {
    width: var(--progress-width);
  }
}

@keyframes pulseGlow {
  0%, 100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.05);
  }
}

@keyframes staggerFadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@layer components {
  .church-button-primary {
    @apply bg-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,60%)] text-white font-medium py-3 px-6 rounded-lg transition-colors flex items-center justify-center;
  }

  .church-button-secondary {
    @apply bg-slate-600 hover:bg-slate-700 text-white font-medium py-3 px-6 rounded-lg transition-colors flex items-center justify-center;
  }

  /* Dashboard Animation Classes */
  .animate-fade-in {
    animation: fadeIn 0.6s ease-out forwards;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out forwards;
  }

  .animate-scale-in {
    animation: scaleIn 0.4s ease-out forwards;
  }

  .animate-counter {
    animation: countUp 1.2s ease-out forwards;
  }

  .stat-card-hover {
    @apply transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-lg hover:-translate-y-1;
  }

  .member-item-hover {
    @apply transition-all duration-200 ease-in-out hover:scale-105 hover:shadow-md hover:bg-slate-50;
  }

  .progress-bar {
    animation: progressFill 2s ease-out forwards;
  }

  .pulse-icon {
    animation: pulseGlow 2s ease-in-out infinite;
  }

  .stagger-item {
    opacity: 0;
    transform: translateY(20px);
    animation: staggerFadeIn 0.6s ease-out forwards;
  }

  .church-button-outline {
    @apply border-2 border-[hsl(258,90%,66%)] text-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,66%)] hover:text-white font-medium py-2 px-4 rounded-lg transition-colors flex items-center justify-center;
  }

  .church-button-error {
    @apply bg-[hsl(0,84%,60%)] hover:bg-[hsl(0,84%,54%)] text-white font-medium py-2 px-4 rounded-lg transition-colors flex items-center justify-center;
  }

  .church-form-input {
    @apply w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-[hsl(258,90%,66%)] focus:border-transparent transition-colors;
  }

  .church-tab-button {
    @apply flex items-center py-4 px-6 text-slate-600 hover:text-slate-900 border-b-2 border-transparent font-medium transition-colors;
  }

  .church-tab-button.active {
    @apply text-[hsl(258,90%,66%)] border-[hsl(258,90%,66%)];
  }

  .church-card {
    @apply bg-white rounded-xl shadow-sm border border-slate-200;
  }

  .church-stat-card {
    @apply bg-white rounded-xl shadow-sm border border-slate-200 p-6;
  }
}

