  const handleViewChurch = async (churchId: string) => {
    try {
      const response = await fetch(`/api/super-admin/churches/${churchId}`, {
        headers: getAuthHeaders(),
      });

      if (response.ok) {
        const churchDetails = await response.json();
        setSelectedChurch(churchDetails);
