import React, { useState } from 'react';
import { Link, useLocation } from 'wouter';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Church, CheckCircle, Loader2, AlertCircle } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';

const registrationSchema = z.object({
  churchName: z.string().min(1, "Church name is required"),
  adminFirstName: z.string().min(1, "First name is required"),
  adminLastName: z.string().min(1, "Last name is required"),
  adminEmail: z.string().email("Invalid email format"),
  password: z.string().min(8, "Password must be at least 8 characters"),
  confirmPassword: z.string(),
  subdomain: z.string().min(3, "Subdomain must be at least 3 characters").regex(/^[a-z0-9-]+$/, "Subdomain can only contain lowercase letters, numbers, and hyphens").optional().or(z.literal("")),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type RegistrationForm = z.infer<typeof registrationSchema>;

const RegisterPage = () => {
  const [, setLocation] = useLocation();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors },
  } = useForm<RegistrationForm>({
    resolver: zodResolver(registrationSchema),
  });

  const churchName = watch('churchName');

  // Auto-generate subdomain from church name
  React.useEffect(() => {
    if (churchName && !watch('subdomain')) {
      const generatedSubdomain = churchName
        .toLowerCase()
        .replace(/[^a-z0-9\s-]/g, '')
        .replace(/\s+/g, '-')
        .replace(/-+/g, '-')
        .replace(/^-|-$/g, '')
        .substring(0, 50);

      setValue('subdomain', generatedSubdomain);
    }
  }, [churchName, setValue, watch]);

  const onSubmit = async (data: RegistrationForm) => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await apiRequest('/api/churches/register', {
        method: 'POST',
        body: JSON.stringify({
          churchName: data.churchName,
          adminFirstName: data.adminFirstName,
          adminLastName: data.adminLastName,
          adminEmail: data.adminEmail,
          password: data.password,
          subdomain: data.subdomain || undefined,
        }),
      });

      if (response.success) {
        // Store authentication token
        localStorage.setItem('auth_token', response.token);
        localStorage.setItem('church_data', JSON.stringify(response.church));
        localStorage.setItem('user_data', JSON.stringify(response.user));

        setSuccess(true);

        // Redirect to main app after short delay
        setTimeout(() => {
          setLocation('/dashboard');
        }, 2000);
      }
    } catch (err: any) {
      console.error('Registration error:', err);
      setError(err.message || 'Registration failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  if (success) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
        <Card className="w-full max-w-md text-center">
          <CardHeader>
            <div className="mx-auto w-16 h-16 bg-green-100 dark:bg-green-900 rounded-full flex items-center justify-center mb-4">
              <CheckCircle className="w-8 h-8 text-green-600 dark:text-green-400" />
            </div>
            <CardTitle className="text-2xl text-green-800 dark:text-green-200">Welcome to ChurchConnect!</CardTitle>
            <CardDescription>
              Your church has been successfully registered and your 30-day free trial has started.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-gray-600 dark:text-gray-300 mb-4">
              Redirecting you to your dashboard...
            </p>
            <div className="flex justify-center">
              <Loader2 className="w-6 h-6 animate-spin text-indigo-600" />
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <Link href="/">
            <div className="inline-flex items-center space-x-2 mb-4 hover:opacity-80 transition-opacity">
              <Church className="h-8 w-8 text-indigo-600 dark:text-indigo-400" />
              <span className="text-2xl font-bold text-gray-900 dark:text-white">ChurchConnect</span>
            </div>
          </Link>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
            Start Your Free Trial
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            Get full access to all features for 30 days. No credit card required.
          </p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Register Your Church</CardTitle>
            <CardDescription>
              Create your church account and start managing your congregation today.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
              {/* Church Information */}
              <div className="space-y-4">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white border-b pb-2">
                  Church Information
                </h3>

                <div>
                  <Label htmlFor="churchName">Church Name *</Label>
                  <Input
                    id="churchName"
                    {...register('churchName')}
                    placeholder="Grace Community Church"
                    className={errors.churchName ? 'border-red-500' : ''}
                  />
                  {errors.churchName && (
                    <p className="text-sm text-red-500 mt-1">{errors.churchName.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="subdomain">Church Subdomain (Optional)</Label>
                  <div className="flex">
                    <Input
                      id="subdomain"
                      {...register('subdomain')}
                      placeholder="grace-community"
                      className={`rounded-r-none ${errors.subdomain ? 'border-red-500' : ''}`}
                    />
                    <div className="px-3 py-2 bg-gray-100 dark:bg-gray-700 border border-l-0 rounded-r-md text-sm text-gray-600 dark:text-gray-300">
                      .churchconnect.app
                    </div>
                  </div>
                  {errors.subdomain && (
                    <p className="text-sm text-red-500 mt-1">{errors.subdomain.message}</p>
                  )}
                  <p className="text-xs text-gray-500 mt-1">
                    This will be your church's unique web address
                  </p>
                </div>
              </div>

              {/* Admin User Information */}
              <div className="space-y-4">
                <h3 className="text-lg font-medium text-gray-900 dark:text-white border-b pb-2">
                  Admin Account
                </h3>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="adminFirstName">First Name *</Label>
                    <Input
                      id="adminFirstName"
                      {...register('adminFirstName')}
                      placeholder="John"
                      className={errors.adminFirstName ? 'border-red-500' : ''}
                    />
                    {errors.adminFirstName && (
                      <p className="text-sm text-red-500 mt-1">{errors.adminFirstName.message}</p>
                    )}
                  </div>

                  <div>
                    <Label htmlFor="adminLastName">Last Name *</Label>
                    <Input
                      id="adminLastName"
                      {...register('adminLastName')}
                      placeholder="Smith"
                      className={errors.adminLastName ? 'border-red-500' : ''}
                    />
                    {errors.adminLastName && (
                      <p className="text-sm text-red-500 mt-1">{errors.adminLastName.message}</p>
                    )}
                  </div>
                </div>

                <div>
                  <Label htmlFor="adminEmail">Email Address *</Label>
                  <Input
                    id="adminEmail"
                    type="email"
                    {...register('adminEmail')}
                    placeholder="pastor@gracechurch.org"
                    className={errors.adminEmail ? 'border-red-500' : ''}
                  />
                  {errors.adminEmail && (
                    <p className="text-sm text-red-500 mt-1">{errors.adminEmail.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="password">Password *</Label>
                  <Input
                    id="password"
                    type="password"
                    {...register('password')}
                    placeholder="Minimum 8 characters"
                    className={errors.password ? 'border-red-500' : ''}
                  />
                  {errors.password && (
                    <p className="text-sm text-red-500 mt-1">{errors.password.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="confirmPassword">Confirm Password *</Label>
                  <Input
                    id="confirmPassword"
                    type="password"
                    {...register('confirmPassword')}
                    placeholder="Confirm your password"
                    className={errors.confirmPassword ? 'border-red-500' : ''}
                  />
                  {errors.confirmPassword && (
                    <p className="text-sm text-red-500 mt-1">{errors.confirmPassword.message}</p>
                  )}
                </div>
              </div>

              {error && (
                <Alert variant="destructive">
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              <Button type="submit" className="w-full" disabled={isLoading}>
                {isLoading ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating Your Church...
                  </>
                ) : (
                  <>
                    Start 30-Day Free Trial
                    <CheckCircle className="w-4 h-4 ml-2" />
                  </>
                )}
              </Button>

              <div className="text-center text-sm text-gray-600 dark:text-gray-300">
                Already have an account?{' '}
                <Link href="/login" className="text-indigo-600 dark:text-indigo-400 hover:underline">
                  Sign in here
                </Link>
              </div>
            </form>
          </CardContent>
        </Card>

        {/* Trial Benefits */}
        <Card className="mt-6 bg-indigo-50 dark:bg-indigo-900/20 border-indigo-200 dark:border-indigo-800">
          <CardContent className="pt-6">
            <h3 className="font-medium text-indigo-900 dark:text-indigo-100 mb-3">
              Your 30-Day Trial Includes:
            </h3>
            <ul className="space-y-2 text-sm text-indigo-800 dark:text-indigo-200">
              <li className="flex items-center">
                <CheckCircle className="w-4 h-4 mr-2 text-indigo-600" />
                Unlimited members and features
              </li>
              <li className="flex items-center">
                <CheckCircle className="w-4 h-4 mr-2 text-indigo-600" />
                Biometric fingerprint check-in
              </li>
              <li className="flex items-center">
                <CheckCircle className="w-4 h-4 mr-2 text-indigo-600" />
                Advanced analytics and reporting
              </li>
              <li className="flex items-center">
                <CheckCircle className="w-4 h-4 mr-2 text-indigo-600" />
                Email and SMS notifications
              </li>
              <li className="flex items-center">
                <CheckCircle className="w-4 h-4 mr-2 text-indigo-600" />
                Priority support and setup assistance
              </li>
            </ul>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default RegisterPage;

client/src/pages/subscription.tsx.content:
import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import { 
  Calendar, 
  Clock, 
  CreditCard, 
  Users, 
  CheckCircle, 
  AlertCircle,
  Crown,
  Zap,
  Shield,
  Loader2,
  ExternalLink,
  ArrowLeft
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';

interface SubscriptionStatus {
  church: {
    id: string;
    name: string;
    subscriptionTier: string;
    maxMembers: number;
    memberCount: number;
    memberUsagePercent: number;
    isTrialActive: boolean;
    trialDaysRemaining: number;
    trialEndDate: string;
  };
  subscription: {
    id: string;
    status: string;
    planId: string;
    currentPeriodStart: string;
    currentPeriodEnd: string;
    cancelAtPeriodEnd: boolean;
  } | null;
  planDetails: {
    priceId: string;
    maxMembers: number;
    features: string[];
    monthlyPrice: number;
  } | null;
  availablePlans: Array<{
    id: string;
    name: string;
    monthlyPrice: number;
    maxMembers: number;
    features: string[];
  }>;
}

const SubscriptionPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const queryClient = useQueryClient();

  const { data: subscriptionStatus, isLoading: statusLoading } = useQuery<SubscriptionStatus>({
    queryKey: ['subscription-status'],
    queryFn: async () => {
      return await apiRequest('/api/subscriptions/status');
    },
  });

  const createCheckoutMutation = useMutation({
    mutationFn: async ({ planId }: { planId: string }) => {
      return await apiRequest('/api/subscriptions/checkout', {
        method: 'POST',
        body: JSON.stringify({
          planId,
          successUrl: `${window.location.origin}/subscription?success=true`,
          cancelUrl: `${window.location.origin}/subscription?canceled=true`,
        }),
      });
    },
    onSuccess: (data) => {
      // Redirect to Stripe Checkout
      window.location.href = data.url;
    },
  });

  const createPortalMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('/api/subscriptions/portal', {
        method: 'POST',
        body: JSON.stringify({
          returnUrl: window.location.href,
        }),
      });
    },
    onSuccess: (data) => {
      // Open billing portal in new tab
      window.open(data.url, '_blank');
    },
  });

  const cancelSubscriptionMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('/api/subscriptions/cancel', {
        method: 'POST',
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['subscription-status'] });
    },
  });

  const reactivateSubscriptionMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('/api/subscriptions/reactivate', {
        method: 'POST',
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['subscription-status'] });
    },
  });

  if (statusLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-indigo-600" />
      </div>
    );
  }

  if (!subscriptionStatus) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>Failed to load subscription information</AlertDescription>
        </Alert>
      </div>
    );
  }

  const { church, subscription, availablePlans } = subscriptionStatus;

  const handleSubscribe = (planId: string) => {
    createCheckoutMutation.mutate({ planId });
  };

  const handleManageBilling = () => {
    createPortalMutation.mutate();
  };

  const handleCancelSubscription = () => {
    if (confirm('Are you sure you want to cancel your subscription? It will remain active until the end of the current billing period.')) {
      cancelSubscriptionMutation.mutate();
    }
  };

  const handleReactivateSubscription = () => {
    reactivateSubscriptionMutation.mutate();
  };

  const formatPrice = (cents: number) => {
    return `$${(cents / 100).toFixed(0)}`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getPlanIcon = (planId: string) => {
    switch (planId) {
      case 'starter': return <Users className="w-5 h-5" />;
      case 'growth': return <Zap className="w-5 h-5" />;
      case 'enterprise': return <Crown className="w-5 h-5" />;
      default: return <Shield className="w-5 h-5" />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
      <div className="max-w-6xl mx-auto">
        {/* Back Navigation */}
        <div className="mb-6">
          <Button 
            variant="ghost" 
            onClick={() => window.history.back()}
            className="flex items-center gap-2 text-gray-600 hover:text-gray-900 dark:text-gray-400 dark:hover:text-white"
          >
            <ArrowLeft className="h-4 w-4" />
            Back to Dashboard
          </Button>
        </div>

        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
            Subscription Management
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            Manage your church's subscription and billing settings
          </p>
        </div>

        {/* Current Status */}
        <div className="grid md:grid-cols-2 gap-6 mb-8">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                {getPlanIcon(church.subscriptionTier)}
                {church.subscriptionTier === 'trial' ? 'Free Trial' : 
                 church.subscriptionTier.charAt(0).toUpperCase() + church.subscriptionTier.slice(1)}
              </CardTitle>
              <CardDescription>Current subscription status</CardDescription>
            </CardHeader>
            <CardContent>
              {church.isTrialActive ? (
                <div className="space-y-4">
                  <Alert>
                    <Clock className="h-4 w-4" />
                    <AlertDescription>
                      <strong>{church.trialDaysRemaining} days remaining</strong> in your free trial.
                      Trial ends on {formatDate(church.trialEndDate)}.
                    </AlertDescription>
                  </Alert>
                  <div>
                    <p className="text-sm text-gray-600 dark:text-gray-300 mb-2">Trial Progress</p>
                    <Progress value={((30 - church.trialDaysRemaining) / 30) * 100} className="w-full" />
                  </div>
                </div>
              ) : (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-300">Status</span>
                    <Badge variant={subscription?.status === 'active' ? 'default' : 'destructive'}>
                      {subscription?.status || 'No subscription'}
                    </Badge>
                  </div>
                  {subscription && (
                    <>
                      <div className="flex items-center justify-between">
                        <span className="text-sm text-gray-600 dark:text-gray-300">Next billing</span>
                        <span className="text-sm font-medium">
                          {formatDate(subscription.currentPeriodEnd)}
                        </span>
                      </div>
                      {subscription.cancelAtPeriodEnd && (
                        <Alert variant="destructive">
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>
                            Your subscription will be canceled on {formatDate(subscription.currentPeriodEnd)}.
                          </AlertDescription>
                        </Alert>
                      )}
                    </>
                  )}
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Member Usage</CardTitle>
              <CardDescription>Current member count and limits</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600 dark:text-gray-300">Members</span>
                  <span className="text-sm font-medium">
                    {church.memberCount} / {church.maxMembers === 999999 ? 'âˆž' : church.maxMembers}
                  </span>
                </div>
                {church.maxMembers !== 999999 && (
                  <div>
                    <Progress value={church.memberUsagePercent} className="w-full" />
                    <p className="text-xs text-gray-500 mt-1">
                      {church.memberUsagePercent}% of limit used
                    </p>
                  </div>
                )}
                {church.memberUsagePercent > 90 && church.maxMembers !== 999999 && (
                  <Alert>
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription>
                      You're approaching your member limit. Consider upgrading to add more members.
                    </AlertDescription>
                  </Alert>
                )}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Subscription Actions */}
        {subscription && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle>Manage Subscription</CardTitle>
              <CardDescription>Update your billing information and subscription settings</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="flex flex-wrap gap-4">
                <Button 
                  onClick={handleManageBilling}
                  disabled={createPortalMutation.isPending}
                  className="flex items-center gap-2"
                >
                  {createPortalMutation.isPending ? (
                    <Loader2 className="w-4 h-4 animate-spin" />
                  ) : (
                    <CreditCard className="w-4 h-4" />
                  )}
                  Manage Billing
                  <ExternalLink className="w-4 h-4" />
                </Button>

                {subscription.cancelAtPeriodEnd ? (
                  <Button 
                    variant="outline"
                    onClick={handleReactivateSubscription}
                    disabled={reactivateSubscriptionMutation.isPending}
                  >
                    {reactivateSubscriptionMutation.isPending ? (
                      <Loader2 className="w-4 h-4 animate-spin mr-2" />
                    ) : null}
                    Reactivate Subscription
                  </Button>
                ) : (
                  <Button 
                    variant="destructive"
                    onClick={handleCancelSubscription}
                    disabled={cancelSubscriptionMutation.isPending}
                  >
                    {cancelSubscriptionMutation.isPending ? (
                      <Loader2 className="w-4 h-4 animate-spin mr-2" />
                    ) : null}
                    Cancel Subscription
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Available Plans */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
            {subscription ? 'Upgrade or Change Plan' : 'Choose Your Plan'}
          </h2>
          <div className="grid md:grid-cols-3 gap-6">
            {availablePlans.map((plan) => (
              <Card 
                key={plan.id} 
                className={`relative ${
                  plan.id === church.subscriptionTier && !church.isTrialActive 
                    ? 'border-indigo-500 ring-2 ring-indigo-200' 
                    : ''
                }`}
              >
                {plan.id === 'growth' && (
                  <Badge className="absolute -top-3 left-1/2 transform -translate-x-1/2 bg-indigo-600">
                    Most Popular
                  </Badge>
                )}

                {plan.id === church.subscriptionTier && !church.isTrialActive && (
                  <Badge className="absolute -top-3 left-1/2 transform -translate-x-1/2 bg-green-600">
                    Current Plan
                  </Badge>
                )}

                <CardHeader className="text-center">
                  <div className="flex justify-center mb-4">
                    {getPlanIcon(plan.id)}
                  </div>
                  <CardTitle className="text-2xl">{plan.name}</CardTitle>
                  <div className="flex items-center justify-center">
                    <span className="text-4xl font-bold">{formatPrice(plan.monthlyPrice)}</span>
                    <span className="text-gray-500 dark:text-gray-400">/month</span>
                  </div>
                </CardHeader>
                <CardContent>
                  <ul className="space-y-3 mb-6">
                    <li className="flex items-center">
                      <CheckCircle className="h-4 w-4 text-green-500 mr-2 flex-shrink-0" />
                      <span className="text-sm">
                        {plan.maxMembers === 999999 ? 'Unlimited members' : `Up to ${plan.maxMembers} members`}
                      </span>
                    </li>
                    {plan.features.slice(0, 5).map((feature, index) => (
                      <li key={index} className="flex items-center">
                        <CheckCircle className="h-4 w-4 text-green-500 mr-2 flex-shrink-0" />
                        <span className="text-sm capitalize">
                          {feature.replace(/_/g, ' ')}
                        </span>
                      </li>
                    ))}
                    {plan.features.length > 5 && (
                      <li className="text-sm text-gray-500">
                        +{plan.features.length - 5} more features
                      </li>
                    )}
                  </ul>

                  <Button 
                    className="w-full" 
                    variant={plan.id === 'growth' ? 'default' : 'outline'}
                    onClick={() => handleSubscribe(plan.id)}
                    disabled={
                      createCheckoutMutation.isPending || 
                      (plan.id === church.subscriptionTier && !church.isTrialActive)
                    }
                  >
                    {createCheckoutMutation.isPending ? (
                      <Loader2 className="w-4 h-4 animate-spin mr-2" />
                    ) : null}
                    {church.isTrialActive 
                      ? 'Subscribe' 
                      : plan.id === church.subscriptionTier 
                        ? 'Current Plan' 
                        : 'Change Plan'
                    }
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SubscriptionPage;

