            </Button>

            <Link href="/first-timer">
              <Button 
                variant="outline"
                className="w-full py-4 border-dashed border-2 border-[hsl(258,90%,66%)] text-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,66%)] hover:text-white"
              >
                <UserPlus className="h-4 w-4 mr-2" />
                First-Time Visitor
              </Button>
            </Link>
          </div>

          <div className="text-center text-xs text-slate-500 mt-6">
            <p>Having trouble? Ask a volunteer for help.</p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { FingerprintScanner } from "@/components/ui/fingerprint-scanner";
import { useToast } from "@/hooks/use-toast";
import { AttendanceStats, CheckInResult, MemberWithChildren } from "@/lib/types";
import { Search, Users, Check, UserPlus, Baby, UserCheck, X, AlertCircle, Fingerprint, Download, Trash2 } from "lucide-react";

export default function CheckInTab() {
  const [searchQuery, setSearchQuery] = useState("");
  const [isScanning, setIsScanning] = useState(false);
  const [selectedParent, setSelectedParent] = useState<MemberWithChildren | null>(null);
  const [selectedChildren, setSelectedChildren] = useState<string[]>([]);
  const [isFamilyDialogOpen, setIsFamilyDialogOpen] = useState(false);
  const [parentChildren, setParentChildren] = useState<MemberWithChildren[]>([]);
  const [attendanceFilter, setAttendanceFilter] = useState<string | null>(null); // For filtering recent check-ins
  const [showDeleteConfirm, setShowDeleteConfirm] = useState<string | null>(null); // Record ID to delete
  const [selectedRecords, setSelectedRecords] = useState<string[]>([]); // For bulk operations
  const [showBulkDeleteConfirm, setShowBulkDeleteConfirm] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get today's attendance stats
  const { data: attendanceStats } = useQuery<AttendanceStats>({
    queryKey: ['/api/attendance/stats'],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Get today's attendance records with member details
  const { data: todayAttendance = [] } = useQuery<any[]>({
    queryKey: ['/api/attendance/today'],
    refetchInterval: 10000, // Refresh every 10 seconds
  });

  // Search members
  const { data: searchResults = [] } = useQuery<MemberWithChildren[]>({
    queryKey: ['/api/members', searchQuery],
    enabled: searchQuery.length > 0,
    queryFn: () => apiRequest('GET', `/api/members?search=${encodeURIComponent(searchQuery)}`).then(res => res.json()),
  });

  // States for enrollment flow
  const [showEnrollmentDialog, setShowEnrollmentDialog] = useState(false);
  const [scannedFingerprintId, setScannedFingerprintId] = useState<string | null>(null);
  const [memberToEnroll, setMemberToEnroll] = useState<MemberWithChildren | null>(null);

  // Biometric scan mutation for check-in
  const biometricScanMutation = useMutation({
    mutationFn: async (fingerprintId: string) => {
      const response = await apiRequest('POST', '/api/fingerprint/scan', { 
        fingerprintId,
        deviceId: navigator.userAgent + navigator.language + screen.width 
      });
      return response.json() as Promise<CheckInResult>;
    },
    onSuccess: (result) => {
      setIsScanning(false);
      if (result.checkInSuccess && result.member) {
        toast({
          title: "Check-in Successful!",
          description: `Welcome, ${result.member.firstName} ${result.member.surname}`,
        });
        queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      } else if (result.isDuplicate && result.member) {
        // Member already checked in today
        toast({
          title: "Already Checked In",
          description: `${result.member.firstName} ${result.member.surname} has already checked in today`,
          variant: "destructive",
        });
      } else {
        // Fingerprint not recognized - offer enrollment opportunity
        setScannedFingerprintId(result.scannedFingerprintId || null);
        setShowEnrollmentDialog(true);
      }
    },
    onError: () => {
      setIsScanning(false);
      toast({
        title: "Scan Failed",
        description: "Please try again or use manual check-in",
        variant: "destructive",
      });
    },
  });

  // Manual check-in mutation
  const manualCheckInMutation = useMutation({
    mutationFn: async (memberId: string) => {
      const today = new Date().toISOString().split('T')[0];
      const response = await apiRequest('POST', '/api/attendance', {
        memberId,
        attendanceDate: today,
        checkInMethod: "manual",
        isGuest: false,
      });
      return response.json();
    },
    onSuccess: (_, memberId) => {
      const member = searchResults.find(m => m.id === memberId);
      toast({
        title: "Check-in Successful!",
        description: `${member?.firstName} ${member?.surname} has been checked in`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      setSearchQuery("");
    },
    onError: (error: any) => {
      // Check if this is a duplicate check-in error
      if (error?.isDuplicate) {
        toast({
          title: "Already Checked In",
          description: error.message || "This person has already checked in today",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Check-in Failed",
          description: "Please try again",
          variant: "destructive",
        });
      }
    },
  });

  // Get children for a parent
  const { data: childrenData } = useQuery<MemberWithChildren[]>({
    queryKey: ['/api/members/children', selectedParent?.id],
    enabled: !!selectedParent?.id,
    staleTime: 0,
  });

  // Quick enrollment mutation for unrecognized fingerprints
  const quickEnrollMutation = useMutation({
    mutationFn: async (data: { memberId: string; fingerprintId: string }) => {
      const response = await apiRequest('POST', '/api/fingerprint/enroll', data);
      return response.json();
    },
    onSuccess: (_, variables) => {
      // After enrollment, automatically check in the member
      const today = new Date().toISOString().split('T')[0];
      return apiRequest('POST', '/api/attendance', {
        memberId: variables.memberId,
        attendanceDate: today,
        checkInMethod: "fingerprint",
        isGuest: false,
      }).then(() => {
        toast({
          title: "Enrollment & Check-in Complete!",
          description: `${memberToEnroll?.firstName} ${memberToEnroll?.surname} enrolled and checked in successfully`,
        });
        queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
        setShowEnrollmentDialog(false);
        setMemberToEnroll(null);
        setScannedFingerprintId(null);
      }).catch((error: any) => {
        // Handle duplicate check-in after enrollment
        if (error?.isDuplicate) {
          toast({
            title: "Enrollment Complete - Already Checked In",
            description: `${memberToEnroll?.firstName} ${memberToEnroll?.surname} was enrolled but has already checked in today`,
            variant: "destructive",
          });
        } else {
          throw error; // Re-throw for the main error handler
        }
        setShowEnrollmentDialog(false);
        setMemberToEnroll(null);
        setScannedFingerprintId(null);
      });
    },
    onError: () => {
      toast({
        title: "Enrollment Failed",
        description: "Please try again or use manual check-in",
        variant: "destructive",
      });
    },
  });

  // Family check-in mutation
  const familyCheckInMutation = useMutation({
    mutationFn: async (data: { parentId: string; childrenIds: string[] }) => {
      const response = await apiRequest('POST', '/api/attendance/selective-family-checkin', data);
      return response.json();
    },
    onSuccess: (result) => {
      toast({
        title: "Family Check-in Successful!",
        description: `${result.parent.firstName} ${result.parent.surname} and ${result.children.length} children checked in`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      setSearchQuery("");
      setIsFamilyDialogOpen(false);
      setSelectedParent(null);
      setSelectedChildren([]);
    },
    onError: () => {
      toast({
        title: "Family Check-in Failed",
        description: "Please try again",
        variant: "destructive",
      });
    },
  });

  const handleBiometricScan = (fingerprintId: string) => {
    setIsScanning(true);
    biometricScanMutation.mutate(fingerprintId);
  };

  const handleBiometricError = (error: string) => {
    setIsScanning(false);
    toast({
      title: "Biometric Scan Error",
      description: error,
      variant: "destructive",
    });
  };

  const handleManualCheckIn = (memberId: string) => {
    manualCheckInMutation.mutate(memberId);
  };

  const handleFamilyCheckIn = async (parentId: string) => {
    // Find the parent member
    const parent = searchResults.find(m => m.id === parentId);
    if (!parent) return;

    // Fetch children for this parent
    try {
      const response = await apiRequest('GET', `/api/members/children/${parentId}`);
      const children = await response.json();

      if (children.length === 0) {
        toast({
          title: "No Children Found",
          description: `${parent.firstName} ${parent.surname} has no linked children`,
          variant: "destructive",
        });
        return;
      }

      setSelectedParent(parent);
      setParentChildren(children);
      setSelectedChildren(children.map((child: MemberWithChildren) => child.id)); // Pre-select all children
      setIsFamilyDialogOpen(true);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load children",
        variant: "destructive",
      });
    }
  };

  const handleConfirmFamilyCheckIn = () => {
    if (!selectedParent) return;

    familyCheckInMutation.mutate({
      parentId: selectedParent.id,
      childrenIds: selectedChildren,
    });
  };

  const toggleChildSelection = (childId: string) => {
    setSelectedChildren(prev => 
      prev.includes(childId) 
        ? prev.filter(id => id !== childId)
        : [...prev, childId]
    );
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const formatTodayDate = () => {
    return new Date().toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // Delete attendance record mutation
  const deleteAttendanceMutation = useMutation({
    mutationFn: async (recordId: string) => {
      const response = await apiRequest('DELETE', `/api/attendance/${recordId}`);
      return response.json();
    },
    onSuccess: (result, recordId) => {
      // Find the record to get member name for toast
      const deletedRecord = todayAttendance.find(r => r.id === recordId);
      const memberName = deletedRecord?.member ? 
        `${deletedRecord.member.firstName} ${deletedRecord.member.surname}` : 
        'Member';

      toast({
        title: "Check-in Deleted",
        description: `${memberName}'s check-in record has been removed`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      setShowDeleteConfirm(null);
    },
    onError: () => {
      toast({
        title: "Delete Failed",
        description: "Could not delete the check-in record. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Bulk delete mutation
  const bulkDeleteMutation = useMutation({
    mutationFn: async (recordIds: string[]) => {
      const deletePromises = recordIds.map(id => 
        apiRequest('DELETE', `/api/attendance/${id}`).then(r => r.json())
      );
      return Promise.all(deletePromises);
    },
    onSuccess: (results, recordIds) => {
      toast({
        title: "Records Deleted",
        description: `${recordIds.length} check-in records have been removed`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      setSelectedRecords([]);
      setShowBulkDeleteConfirm(false);
    },
    onError: () => {
      toast({
        title: "Bulk Delete Failed",
        description: "Could not delete all selected records. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Handle bulk selection
  const toggleRecordSelection = (recordId: string) => {
    setSelectedRecords(prev => 
      prev.includes(recordId) 
        ? prev.filter(id => id !== recordId)
        : [...prev, recordId]
    );
  };

  const selectAllRecords = () => {
    const filteredRecords = attendanceFilter 
      ? todayAttendance.filter((record: any) => {
          const member = record.member;
          if (!member) return false;

          if (attendanceFilter === 'male' || attendanceFilter === 'female') {
            return member.gender === attendanceFilter;
          }
          if (attendanceFilter === 'child' || attendanceFilter === 'adolescent' || attendanceFilter === 'adult') {
            return member.ageGroup === attendanceFilter;
          }
          return true;
        })
      : todayAttendance;

    const allIds = filteredRecords.map((record: any) => record.id);
    setSelectedRecords(allIds);
  };

  const clearSelection = () => {
    setSelectedRecords([]);
  };

  // Export today's attendance as CSV
  const exportTodayAttendance = () => {
    const today = new Date().toISOString().split('T')[0];
    const formattedDate = new Date().toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });

    // Filter data if a filter is active
    const dataToExport = attendanceFilter 
      ? todayAttendance.filter((record: any) => {
          const member = record.member;
          if (!member) return false;

          if (attendanceFilter === 'male' || attendanceFilter === 'female') {
            return member.gender === attendanceFilter;
          }
          if (attendanceFilter === 'child' || attendanceFilter === 'adolescent' || attendanceFilter === 'adult') {
            return member.ageGroup === attendanceFilter;
          }
          return true;
        })
      : todayAttendance;

    // Prepare CSV data
    const csvHeaders = ['Name', 'Gender', 'Age Group', 'Check-in Time', 'Method', 'Phone', 'Email'];
    const csvData = dataToExport.map((record: any) => [
      `${record.member?.firstName || ''} ${record.member?.surname || ''}`.trim(),
      record.member?.gender || '',
      record.member?.ageGroup || '',
      new Date(record.checkInTime).toLocaleString('en-US'),
      record.checkInMethod || '',
      record.member?.phone || '',
      record.member?.email || ''
    ]);

    // Create CSV content with filter info if applicable
    const filterText = attendanceFilter ? ` (${attendanceFilter.charAt(0).toUpperCase() + attendanceFilter.slice(1)} Only)` : '';
    const csvContent = [
      [`Church Attendance Report - ${formattedDate}${filterText}`],
      [`Total ${attendanceFilter ? `${attendanceFilter} ` : ''}Attendance: ${dataToExport.length}`],
      ['Summary Statistics:'],
      [`Male: ${attendanceStats?.male || 0}, Female: ${attendanceStats?.female || 0}`],
      [`Adults: ${attendanceStats?.adult || 0}, Children: ${attendanceStats?.child || 0}, Adolescents: ${attendanceStats?.adolescent || 0}`],
      [''], // Empty row
      csvHeaders,
      ...csvData
    ].map(row => row.join(',')).join('\n');

    // Create and download file
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    if (link.download !== undefined) {
      const url = URL.createObjectURL(blob);
      link.setAttribute('href', url);
      const filename = attendanceFilter 
        ? `attendance-${today}-${attendanceFilter}.csv`
        : `attendance-${today}.csv`;
      link.setAttribute('download', filename);
      link.style.visibility = 'hidden';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }

    toast({
      title: "Export Complete",
      description: `${attendanceFilter ? `${attendanceFilter.charAt(0).toUpperCase() + attendanceFilter.slice(1)} ` : ''}attendance report downloaded with ${dataToExport.length} records`,
    });
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
      {/* Fingerprint Scanner */}
      <div className="lg:col-span-2">
        <Card className="church-card">
          <CardHeader>
            <CardTitle className="text-2xl font-semibold text-slate-900 text-center">
              Today Check-in
              <div className="text-base font-normal text-slate-600 mt-1">
                {formatTodayDate()}
              </div>
            </CardTitle>
          </CardHeader>
          <CardContent className="p-8">
            <FingerprintScanner
              mode="scan"
              onScanComplete={handleBiometricScan}
              onScanStart={() => setIsScanning(true)}
              onError={handleBiometricError}
              isScanning={isScanning || biometricScanMutation.isPending}
            />

            {/* Manual Override */}
            <div className="mt-8 pt-6 border-t border-slate-200">
              <h4 className="text-lg font-medium text-slate-900 mb-4">Manual Check-In</h4>
              <div className="flex space-x-4">
                <div className="flex-1 relative">
                  <Input
                    type="text"
                    placeholder="Search member name..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="church-form-input"
                  />
                  {searchQuery && searchResults.length > 0 && (
                    <div className="absolute top-full left-0 right-0 bg-white border border-slate-200 rounded-lg shadow-lg mt-1 max-h-60 overflow-y-auto z-10">
                      {searchResults.map((member) => (
                        <div
                          key={member.id}
                          className="p-3 hover:bg-slate-50 border-b border-slate-100 last:border-b-0"
                        >
                          <div className="flex items-center justify-between">
                            <div>
                              <p className="font-medium text-slate-900">
                                {member.firstName} {member.surname}
                              </p>
                              <p className="text-sm text-slate-500">
                                {member.ageGroup} â€¢ {member.phone}
                              </p>
                            </div>
                            <div className="flex space-x-2">
                              <Button
                                size="sm"
                                onClick={() => handleManualCheckIn(member.id)}
                                disabled={manualCheckInMutation.isPending}
                                className="church-button-primary text-xs px-3 py-1"
                              >
                                Check In
                              </Button>
                              {(member.gender === 'male' || member.gender === 'female') && (
                                <Button
                                  size="sm"
                                  onClick={() => handleFamilyCheckIn(member.id)}
                                  disabled={familyCheckInMutation.isPending}
                                  variant="outline"
                                  className="text-xs px-3 py-1"
                                >
                                  <Users className="h-3 w-3 mr-1" />
                                  Family
                                </Button>
                              )}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
                <Button className="church-button-outline">
                  <Search className="h-4 w-4" />
                </Button>
                <Button variant="outline" className="church-button-secondary">
                  <UserPlus className="h-4 w-4 mr-2" />
                  Guest Check-In
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Today's Attendance */}
      <div className="space-y-6">
        <Card className="church-card">
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg font-semibold text-slate-900">Today's Attendance</CardTitle>
            <div className="flex items-center gap-2">
              {attendanceFilter && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setAttendanceFilter(null)}
                  className="text-slate-500 hover:text-slate-700"
                >
                  <X className="h-4 w-4 mr-1" />
                  Clear Filter
                </Button>
              )}
              <Button
                variant="outline"
                size="sm"
                onClick={exportTodayAttendance}
                disabled={todayAttendance.length === 0}
                className="text-green-600 hover:text-green-700 hover:bg-green-50 border-green-300"
              >
                <Download className="h-4 w-4 mr-1" />
                Export CSV
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-slate-600">Total Present</span>
                <span className="text-2xl font-bold text-[hsl(142,76%,36%)]">
                  {attendanceStats?.total || 0}
                </span>
              </div>

              <div className="grid grid-cols-2 gap-4 text-sm">
                <button
                  onClick={() => setAttendanceFilter(attendanceFilter === 'male' ? null : 'male')}
                  className={`text-center p-3 bg-blue-50 rounded-lg transition-all hover:bg-blue-100 hover:shadow-md ${
                    attendanceFilter === 'male' ? 'ring-2 ring-blue-500 bg-blue-100' : ''
                  }`}
                >
                  <div className="text-lg font-semibold text-slate-900">
                    {attendanceStats?.male || 0}
                  </div>
                  <div className="text-slate-600">Male</div>
                </button>
                <button
                  onClick={() => setAttendanceFilter(attendanceFilter === 'female' ? null : 'female')}
                  className={`text-center p-3 bg-pink-50 rounded-lg transition-all hover:bg-pink-100 hover:shadow-md ${
                    attendanceFilter === 'female' ? 'ring-2 ring-pink-500 bg-pink-100' : ''
                  }`}
                >
                  <div className="text-lg font-semibold text-slate-900">
                    {attendanceStats?.female || 0}
                  </div>
                  <div className="text-slate-600">Female</div>
                </button>
                <button
                  onClick={() => setAttendanceFilter(attendanceFilter === 'child' ? null : 'child')}
                  className={`text-center p-3 bg-yellow-50 rounded-lg transition-all hover:bg-yellow-100 hover:shadow-md ${
                    attendanceFilter === 'child' ? 'ring-2 ring-yellow-500 bg-yellow-100' : ''
                  }`}
                >
                  <div className="text-lg font-semibold text-slate-900">
                    {attendanceStats?.child || 0}
                  </div>
                  <div className="text-slate-600">Children</div>
                </button>
                <button
                  onClick={() => setAttendanceFilter(attendanceFilter === 'adolescent' ? null : 'adolescent')}
                  className={`text-center p-3 bg-purple-50 rounded-lg transition-all hover:bg-purple-100 hover:shadow-md ${
                    attendanceFilter === 'adolescent' ? 'ring-2 ring-purple-500 bg-purple-100' : ''
                  }`}
                >
                  <div className="text-lg font-semibold text-slate-900">
                    {attendanceStats?.adolescent || 0}
                  </div>
                  <div className="text-slate-600">Adolescent</div>
                </button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Recent Check-ins */}
        <Card className="church-card">
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg font-semibold text-slate-900">
              Recent Check-ins
              {attendanceFilter && (
                <span className="ml-2 px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded-full capitalize">
                  {attendanceFilter} only
                </span>
              )}
              {selectedRecords.length > 0 && (
                <span className="ml-2 px-2 py-1 bg-purple-100 text-purple-700 text-xs rounded-full">
                  {selectedRecords.length} selected
                </span>
              )}
            </CardTitle>
            <div className="flex items-center gap-2">
              {selectedRecords.length > 0 && (
                <>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={clearSelection}
                    className="text-slate-500 hover:text-slate-700"
                  >
                    <X className="h-4 w-4 mr-1" />
                    Clear
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setShowBulkDeleteConfirm(true)}
                    className="text-red-600 hover:text-red-700 hover:bg-red-50 border-red-300"
                  >
                    <Trash2 className="h-4 w-4 mr-1" />
                    Delete ({selectedRecords.length})
                  </Button>
                </>
              )}
              {todayAttendance.length > 0 && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={exportTodayAttendance}
                  className="text-green-600 hover:text-green-700 hover:bg-green-50"
                >
                  <Download className="h-4 w-4 mr-1" />
                  Export
                </Button>
              )}
            </div>
          </CardHeader>

          {/* Bulk Selection Controls */}
          {todayAttendance.length > 0 && (
            <div className="px-6 pb-4">
              <div className="flex items-center gap-3 text-sm text-slate-600">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={selectAllRecords}
                  className="text-xs px-2 py-1 h-auto"
                >
                  Select All {attendanceFilter ? `(${attendanceFilter})` : ''}
                </Button>
                <span>â€¢</span>
                <span>Click checkboxes to select records for bulk operations</span>
              </div>
            </div>
          )}
          <CardContent>
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {/* Filter attendance records based on selected filter */}
              {todayAttendance
                .filter((record: any) => {
                  if (!attendanceFilter) return true;
                  const member = record.member;
                  if (!member) return false;

                  if (attendanceFilter === 'male' || attendanceFilter === 'female') {
                    return member.gender === attendanceFilter;
                  }
                  if (attendanceFilter === 'child' || attendanceFilter === 'adolescent' || attendanceFilter === 'adult') {
                    return member.ageGroup === attendanceFilter;
                  }
                  return true;
                })
                .slice(0, 10)
                .map((record: any) => (
                  <div key={record.id} className={`flex items-center space-x-3 p-3 rounded-lg border transition-colors group ${
                    selectedRecords.includes(record.id) 
                      ? 'bg-purple-50 border-purple-200' 
                      : 'bg-green-50 border-green-200 hover:bg-green-100'
                  }`}>
                    <Checkbox
                      checked={selectedRecords.includes(record.id)}
                      onCheckedChange={() => toggleRecordSelection(record.id)}
                      className="opacity-60 group-hover:opacity-100 transition-opacity"
                    />
                    <div className="w-8 h-8 bg-[hsl(142,76%,36%)] rounded-full flex items-center justify-center">
                      <Check className="text-white text-sm" />
                    </div>
                    <div className="flex-1">
                      <p className="font-medium text-slate-900">
                        {record.member?.firstName || 'Unknown'} {record.member?.surname || 'Member'}
                      </p>
                      <p className="text-sm text-slate-500">
                        {formatTime(record.checkInTime)} â€¢ {record.isVisitor ? 'visitor' : 'member'}
                        {attendanceFilter && (
                          <span className="ml-2 px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded-full">
                            {record.member?.gender} â€¢ {record.member?.ageGroup}
                          </span>
                        )}
                      </p>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setShowDeleteConfirm(record.id)}
                      className="opacity-0 group-hover:opacity-100 transition-opacity text-red-500 hover:text-red-700 hover:bg-red-50"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                ))}

              {/* Show filtered empty state */}
              {attendanceFilter && todayAttendance.filter((record: any) => {
                const member = record.member;
                if (!member) return false;

                if (attendanceFilter === 'male' || attendanceFilter === 'female') {
                  return member.gender === attendanceFilter;
                }
                if (attendanceFilter === 'child' || attendanceFilter === 'adolescent' || attendanceFilter === 'adult') {
                  return member.ageGroup === attendanceFilter;
                }
                return true;
              }).length === 0 && (
                <div className="text-center py-8">
                  <Users className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                  <p className="text-slate-500">No {attendanceFilter} check-ins yet today</p>
                </div>
              )}

              {/* Show general empty state */}
              {!attendanceFilter && todayAttendance.length === 0 && (
                <div className="text-center py-8">
                  <Users className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                  <p className="text-slate-500">No check-ins yet today</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Family Check-in Selection Dialog */}
      <Dialog open={isFamilyDialogOpen} onOpenChange={setIsFamilyDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center space-x-2">
              <Users className="h-5 w-5 text-[hsl(258,90%,66%)]" />
              <span>Family Check-in</span>
            </DialogTitle>
          </DialogHeader>

          {selectedParent && (
            <div className="space-y-4">
              <div className="p-4 bg-slate-50 rounded-lg">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-[hsl(258,90%,66%)] rounded-full flex items-center justify-center">
                    <UserCheck className="h-5 w-5 text-white" />
                  </div>
                  <div>
                    <p className="font-medium text-slate-900">
                      {selectedParent.firstName} {selectedParent.surname}
                    </p>
                    <p className="text-sm text-slate-500">Parent - Will be checked in</p>
                  </div>
                </div>
              </div>

              <div className="space-y-3">
                <p className="text-sm font-medium text-slate-900">Select children who are present today:</p>

                {parentChildren.map((child) => (
                  <div key={child.id} className="flex items-center space-x-3 p-3 border border-slate-200 rounded-lg hover:bg-slate-50">
                    <Checkbox
                      id={`child-${child.id}`}
                      checked={selectedChildren.includes(child.id)}
                      onCheckedChange={() => toggleChildSelection(child.id)}
                    />
                    <div className="flex items-center space-x-3 flex-1">
                      <div className="w-8 h-8 bg-yellow-100 rounded-full flex items-center justify-center">
                        <Baby className="h-4 w-4 text-yellow-600" />
                      </div>
                      <div>
                        <p className="font-medium text-slate-900">
                          {child.firstName} {child.surname}
                        </p>
                        <p className="text-xs text-slate-500 capitalize">
                          {child.ageGroup} â€¢ Born {child.dateOfBirth}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}

                {parentChildren.length === 0 && (
                  <div className="text-center py-6">
                    <Baby className="h-12 w-12 text-slate-300 mx-auto mb-2" />
                    <p className="text-slate-500">No children linked to this parent</p>
                  </div>
                )}
              </div>

              <div className="flex space-x-2 pt-4">
                <Button
                  onClick={handleConfirmFamilyCheckIn}
                  disabled={familyCheckInMutation.isPending}
                  className="flex-1 bg-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,60%)] text-white"
                >
                  <UserCheck className="h-4 w-4 mr-2" />
                  {familyCheckInMutation.isPending 
                    ? "Checking in..." 
                    : `Check In Family (${selectedChildren.length + 1})`
                  }
                </Button>
                <Button
                  variant="outline"
                  onClick={() => setIsFamilyDialogOpen(false)}
                  disabled={familyCheckInMutation.isPending}
                >
                  <X className="h-4 w-4 mr-2" />
                  Cancel
                </Button>
              </div>

              {selectedChildren.length === 0 && (
                <p className="text-xs text-amber-600 bg-amber-50 p-2 rounded">
                  Note: Only the parent will be checked in since no children are selected.
                </p>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Biometric Enrollment Dialog */}
      <Dialog open={showEnrollmentDialog} onOpenChange={setShowEnrollmentDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <div className="flex items-center justify-center mb-4">
              <div className="w-16 h-16 bg-gradient-to-br from-orange-500 to-red-600 rounded-full flex items-center justify-center">
                <AlertCircle className="h-8 w-8 text-white" />
              </div>
            </div>
            <DialogTitle className="text-center">Fingerprint Not Found</DialogTitle>
            <p className="text-sm text-slate-600 text-center mt-2">
              Your fingerprint wasn't found in our system. Would you like to enroll it now for faster future check-ins?
            </p>
          </DialogHeader>

          <div className="space-y-4">
            {/* Search for existing member */}
            <div>
              <label className="text-sm font-medium text-slate-700 mb-2 block">
                Search for your name to link this fingerprint:
              </label>
              <Input
                placeholder="Type your name..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full"
              />
            </div>

            {searchResults.length > 0 && (
              <div className="border rounded-lg max-h-32 overflow-y-auto">
                {searchResults.slice(0, 5).map((member) => (
                  <button
                    key={member.id}
                    onClick={() => {
                      setMemberToEnroll(member);
                      setSearchQuery("");
                    }}
                    className="w-full p-3 text-left hover:bg-slate-50 border-b last:border-b-0 flex items-center justify-between"
                  >
                    <div>
                      <p className="font-medium text-slate-900">
                        {member.firstName} {member.surname}
                      </p>
                      <p className="text-sm text-slate-500">
                        {member.gender} â€¢ {member.ageGroup} â€¢ {member.phone}
                      </p>
                    </div>
                    <Fingerprint className="h-5 w-5 text-slate-400" />
                  </button>
                ))}
              </div>
            )}

            {memberToEnroll && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="font-medium text-green-800">
                      {memberToEnroll.firstName} {memberToEnroll.surname}
                    </p>
                    <p className="text-sm text-green-600">Ready to enroll fingerprint</p>
                  </div>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => setMemberToEnroll(null)}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            )}
          </div>

          <DialogFooter className="flex-col space-y-2">
            {memberToEnroll && scannedFingerprintId && (
              <Button
                onClick={() => quickEnrollMutation.mutate({
                  memberId: memberToEnroll.id,
                  fingerprintId: scannedFingerprintId
                })}
                disabled={quickEnrollMutation.isPending}
                className="w-full bg-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,60%)] text-white"
              >
                <Fingerprint className="h-4 w-4 mr-2" />
                {quickEnrollMutation.isPending ? "Enrolling..." : "Enroll Fingerprint & Check In"}
              </Button>
            )}

            <div className="flex space-x-2 w-full">
              <Button
                variant="outline"
                onClick={() => {
                  if (memberToEnroll) {
                    manualCheckInMutation.mutate(memberToEnroll.id);
                    setShowEnrollmentDialog(false);
                    setMemberToEnroll(null);
                    setScannedFingerprintId(null);
                  }
                }}
                disabled={!memberToEnroll || manualCheckInMutation.isPending}
                className="flex-1"
              >
                Skip & Manual Check-in
              </Button>

              <Button
                variant="ghost"
                onClick={() => {
                  setShowEnrollmentDialog(false);
                  setMemberToEnroll(null);
                  setScannedFingerprintId(null);
                  setSearchQuery("");
                }}
                className="flex-1"
              >
                Cancel
              </Button>
            </div>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={!!showDeleteConfirm} onOpenChange={() => setShowDeleteConfirm(null)}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <div className="flex items-center justify-center mb-4">
              <div className="w-16 h-16 bg-gradient-to-br from-red-500 to-red-600 rounded-full flex items-center justify-center">
                <Trash2 className="h-8 w-8 text-white" />
              </div>
            </div>
            <DialogTitle className="text-center">Delete Check-in Record?</DialogTitle>
            <p className="text-sm text-slate-600 text-center mt-2">
              {showDeleteConfirm && (() => {
                const record = todayAttendance.find(r => r.id === showDeleteConfirm);
                const memberName = record?.member ? 
                  `${record.member.firstName} ${record.member.surname}` : 
                  'This member';
                return `Are you sure you want to delete ${memberName}'s check-in record? This action cannot be undone.`;
              })()}
            </p>
          </DialogHeader>

          <DialogFooter className="flex-col space-y-2">
            <div className="flex space-x-2 w-full">
              <Button
                variant="outline"
                onClick={() => setShowDeleteConfirm(null)}
                disabled={deleteAttendanceMutation.isPending}
                className="flex-1"
              >
                Cancel
              </Button>

              <Button
                onClick={() => showDeleteConfirm && deleteAttendanceMutation.mutate(showDeleteConfirm)}
                disabled={deleteAttendanceMutation.isPending}
                className="flex-1 bg-red-600 hover:bg-red-700 text-white"
              >
                {deleteAttendanceMutation.isPending ? "Deleting..." : "Delete Record"}
              </Button>
            </div>

            <p className="text-xs text-slate-500 text-center">
              Note: This will remove the check-in record but won't affect the member's profile.
            </p>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Bulk Delete Confirmation Dialog */}
      <Dialog open={showBulkDeleteConfirm} onOpenChange={setShowBulkDeleteConfirm}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <div className="flex items-center justify-center mb-4">
              <div className="w-16 h-16 bg-gradient-to-br from-red-500 to-red-600 rounded-full flex items-center justify-center">
                <Trash2 className="h-8 w-8 text-white" />
              </div>
            </div>
            <DialogTitle className="text-center">Delete {selectedRecords.length} Check-in Records?</DialogTitle>
            <p className="text-sm text-slate-600 text-center mt-2">
              Are you sure you want to delete {selectedRecords.length} selected check-in records? 
              This will remove the records for the following members:
            </p>
          </DialogHeader>

          <div className="max-h-32 overflow-y-auto bg-slate-50 rounded-lg p-3 space-y-1">
            {selectedRecords.slice(0, 10).map(recordId => {
              const record = todayAttendance.find((r: any) => r.id === recordId);
              const memberName = record?.member ? 
                `${record.member.firstName} ${record.member.surname}` : 
                'Unknown Member';
              return (
                <div key={recordId} className="text-sm text-slate-700">
                  â€¢ {memberName} ({formatTime(record?.checkInTime || '')})
                </div>
              );
            })}
            {selectedRecords.length > 10 && (
              <div className="text-sm text-slate-500 italic">
                ...and {selectedRecords.length - 10} more
              </div>
            )}
          </div>

          <DialogFooter className="flex-col space-y-2">
            <div className="flex space-x-2 w-full">
              <Button
                variant="outline"
                onClick={() => setShowBulkDeleteConfirm(false)}
                disabled={bulkDeleteMutation.isPending}
                className="flex-1"
              >
                Cancel
              </Button>

              <Button
                onClick={() => bulkDeleteMutation.mutate(selectedRecords)}
                disabled={bulkDeleteMutation.isPending}
                className="flex-1 bg-red-600 hover:bg-red-700 text-white"
              >
                {bulkDeleteMutation.isPending ? "Deleting..." : `Delete ${selectedRecords.length} Records`}
              </Button>
            </div>

            <p className="text-xs text-slate-500 text-center">
              Warning: This action cannot be undone and will permanently remove all selected records.
            </p>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { AttendanceStats, MemberWithChildren } from "@/lib/types";
import { Users, Calendar, AlertTriangle, TrendingUp, Download, Search, MessageSquare, Mail, CheckCircle, Phone } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function DashboardTab() {
  const [searchQuery, setSearchQuery] = useState("");
  const [groupFilter, setGroupFilter] = useState("all");
  const [statusFilter, setStatusFilter] = useState("all");
  const [followUpTemplate, setFollowUpTemplate] = useState(
    "Hi [Name], we missed you at church today. Hope to see you next Sunday! - Grace Community Church"
  );
  const [isSendingAll, setIsSendingAll] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get attendance stats
  const { data: attendanceStats } = useQuery<AttendanceStats>({
    queryKey: ['/api/attendance/stats'],
  });

  // Get today's attendance details
  const { data: todaysAttendance = [] } = useQuery<any[]>({
    queryKey: ['/api/attendance/today'],
  });

  // Get all members with search and filter
  const { data: members = [] } = useQuery<MemberWithChildren[]>({
    queryKey: ['/api/members'],
  });

  // Get members needing follow-up
  const { data: followUpMembers = [] } = useQuery<any[]>({
    queryKey: ['/api/follow-up'],
  });

  // Mutation for individual follow-up
  const sendFollowUpMutation = useMutation({
    mutationFn: async ({ memberId, method }: { memberId: string; method: 'sms' | 'email' }) => {
      return await apiRequest('POST', `/api/follow-up/${memberId}`, { method });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/follow-up'] });
      toast({
        title: "Follow-up sent",
        description: "Contact has been recorded successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send follow-up",
        variant: "destructive",
      });
    }
  });

  // Function to send individual follow-up
  const handleSendFollowUp = async (memberId: string, method: 'sms' | 'email') => {
    sendFollowUpMutation.mutate({ memberId, method });
  };

  // Function to send all follow-ups
  const handleSendAll = async () => {
    setIsSendingAll(true);
    try {
      const promises = followUpMembers.map(member => 
        apiRequest('POST', `/api/follow-up/${member.id}`, {
          method: member.phone ? 'sms' : 'email'
        })
      );

      await Promise.all(promises);
      queryClient.invalidateQueries({ queryKey: ['/api/follow-up'] });

      toast({
        title: "Bulk follow-up completed",
        description: `Sent ${followUpMembers.length} follow-up messages successfully`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Some follow-ups failed to send",
        variant: "destructive",
      });
    } finally {
      setIsSendingAll(false);
    }
  };

  // Calculate comprehensive stats
  const totalRegisteredMembers = members.length;
  const todaysMemberAttendance = todaysAttendance.filter(record => record.memberId && !record.isVisitor).length;
  const todaysVisitorAttendance = todaysAttendance.filter(record => record.isVisitor).length;
  const totalTodaysAttendance = todaysAttendance.length;

  // Calculate attendance rate based on registered members only
  const memberAttendanceRate = totalRegisteredMembers > 0 
    ? Math.round((todaysMemberAttendance / totalRegisteredMembers) * 100) 
    : 0;

  // Calculate average weekly attendance (estimate based on today's attendance)
  const avgWeeklyAttendance = Math.round(totalTodaysAttendance * 1.2); // More realistic estimate

  const filteredMembers = members.filter(member => {
    if (statusFilter === 'current' && !member.isCurrentMember) return false;
    if (statusFilter === 'new' && member.isCurrentMember) return false;
    return true;
  });

  const handleExportMembers = async () => {
    try {
      const response = await fetch('/api/export/members');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'church_members.csv';
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  const handleExportAttendance = async () => {
    try {
      const response = await fetch('/api/export/attendance');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'attendance_records.csv';
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  const getAttendanceStatus = (member: MemberWithChildren) => {
    // Mock attendance status - in real app this would come from recent attendance data
    const statuses = ['present', 'absent-1', 'absent-2', 'absent-3+'];
    const status = statuses[Math.floor(Math.random() * statuses.length)];

    switch (status) {
      case 'present':
        return { text: 'Present Today', color: 'text-[hsl(142,76%,36%)]' };
      case 'absent-1':
        return { text: 'Absent (1 week)', color: 'text-[hsl(45,93%,47%)]' };
      case 'absent-2':
        return { text: 'Absent (2 weeks)', color: 'text-[hsl(45,93%,47%)]' };
      default:
        return { text: 'Absent (3+ weeks)', color: 'text-[hsl(0,84%,60%)]' };
    }
  };

  return (
    <div className="space-y-8">
      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="church-stat-card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-slate-600">Registered Members</p>
              <p className="text-3xl font-bold text-slate-900">{totalRegisteredMembers}</p>
            </div>
            <div className="w-12 h-12 bg-[hsl(258,90%,66%)]/10 rounded-lg flex items-center justify-center">
              <Users className="text-[hsl(258,90%,66%)] text-xl" />
            </div>
          </div>
          <p className="text-sm text-[hsl(142,76%,36%)] mt-2">
            <TrendingUp className="inline h-3 w-3 mr-1" />
            Enrolled in system
          </p>
        </Card>

        <Card className="church-stat-card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-slate-600">Today's Attendance</p>
              <p className="text-3xl font-bold text-slate-900">{totalTodaysAttendance}</p>
            </div>
            <div className="w-12 h-12 bg-[hsl(142,76%,36%)]/10 rounded-lg flex items-center justify-center">
              <Calendar className="text-[hsl(142,76%,36%)] text-xl" />
            </div>
          </div>
          <p className="text-sm text-blue-600 mt-2">{todaysMemberAttendance} members + {todaysVisitorAttendance} visitors</p>
        </Card>

        <Card className="church-stat-card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-slate-600">Follow-up Needed</p>
              <p className="text-3xl font-bold text-slate-900">{followUpMembers.length}</p>
            </div>
            <div className="w-12 h-12 bg-[hsl(45,93%,47%)]/10 rounded-lg flex items-center justify-center">
              <AlertTriangle className="text-[hsl(45,93%,47%)] text-xl" />
            </div>
          </div>
          <p className="text-sm text-[hsl(45,93%,47%)] mt-2">3+ services missed</p>
        </Card>

        <Card className="church-stat-card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-slate-600">Member Attendance Rate</p>
              <p className="text-3xl font-bold text-slate-900">{memberAttendanceRate}%</p>
            </div>
            <div className="w-12 h-12 bg-[hsl(271,91%,65%)]/10 rounded-lg flex items-center justify-center">
              <TrendingUp className="text-[hsl(271,91%,65%)] text-xl" />
            </div>
          </div>
          <p className="text-sm text-slate-600 mt-2">
            {todaysMemberAttendance} of {totalRegisteredMembers} attended today
          </p>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Member Search & Filter */}
        <Card className="church-card">
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg font-semibold text-slate-900">Member Directory</CardTitle>
            <div className="flex space-x-2">
              <Button onClick={handleExportMembers} variant="outline" size="sm">
                <Download className="mr-2 h-4 w-4" />
                Export CSV
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="space-y-4 mb-6">
              <div className="flex space-x-4">
                <div className="flex-1 relative">
                  <Input
                    type="text"
                    placeholder="Search members..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="church-form-input"
                  />
                  <Search className="absolute right-3 top-3 h-4 w-4 text-slate-400" />
                </div>
                <Button className="church-button-primary">
                  <Search className="h-4 w-4" />
                </Button>
              </div>

              <div className="flex space-x-4">
                <Select value={groupFilter} onValueChange={setGroupFilter}>
                  <SelectTrigger className="church-form-input">
                    <SelectValue placeholder="All Groups" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Groups</SelectItem>
                    <SelectItem value="male">Male</SelectItem>
                    <SelectItem value="female">Female</SelectItem>
                    <SelectItem value="child">Children</SelectItem>
                    <SelectItem value="adolescent">Adolescent</SelectItem>
                  </SelectContent>
                </Select>
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger className="church-form-input">
                    <SelectValue placeholder="All Status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="current">Current Members</SelectItem>
                    <SelectItem value="new">New Members</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-3 max-h-96 overflow-y-auto">
              {filteredMembers.map((member) => {
                const attendanceStatus = getAttendanceStatus(member);
                return (
                  <div key={member.id} className="flex items-center justify-between p-4 border border-slate-200 rounded-lg hover:bg-slate-50">
                    <div className="flex items-center space-x-4">
                      <div className="w-10 h-10 bg-[hsl(258,90%,66%)] rounded-full flex items-center justify-center">
                        <span className="text-white font-medium">
                          {member.firstName[0]}{member.surname[0]}
                        </span>
                      </div>
                      <div>
                        <p className="font-medium text-slate-900">
                          {member.firstName} {member.surname}
                        </p>
                        <p className="text-sm text-slate-500">
                          {member.ageGroup} â€¢ {member.isCurrentMember ? 'Current' : 'New'} Member
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className={`text-sm ${attendanceStatus.color}`}>
                        {attendanceStatus.text}
                      </p>
                      <p className="text-xs text-slate-500">
                        Last: {new Date().toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                );
              })}

              {filteredMembers.length === 0 && (
                <div className="text-center py-8">
                  <Users className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                  <p className="text-slate-500">No members found</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Follow-up Notifications */}
        <Card className="church-card">
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg font-semibold text-slate-900">Follow-up Queue</CardTitle>
            <Button 
              className="church-button-primary" 
              size="sm"
              onClick={handleSendAll}
              disabled={followUpMembers.length === 0 || isSendingAll}
            >
              <MessageSquare className="mr-2 h-4 w-4" />
              {isSendingAll ? 'Sending...' : 'Send All'}
            </Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {followUpMembers.slice(0, 5).map((member: any) => (
                <div key={member.id} className="p-4 bg-[hsl(45,93%,47%)]/5 border border-[hsl(45,93%,47%)]/20 rounded-lg">
                  <div className="flex items-center justify-between mb-3">
                    <div>
                      <p className="font-medium text-slate-900">
                        {member.firstName} {member.surname}
                      </p>
                      <p className="text-sm text-slate-500">
                        {member.group} â€¢ {member.phone}
                      </p>
                    </div>
                    <span className="px-2 py-1 bg-[hsl(0,84%,60%)]/10 text-[hsl(0,84%,60%)] text-sm rounded-full">
                      {member.followUpRecord?.consecutiveAbsences || 3} weeks absent
                    </span>
                  </div>
                  <p className="text-sm text-slate-600 mb-3">
                    Last attended: {new Date(Date.now() - 21 * 24 * 60 * 60 * 1000).toLocaleDateString()}
                  </p>
                  <div className="flex space-x-2">
                    <Button 
                      size="sm" 
                      variant="outline" 
                      className="text-xs"
                      onClick={() => handleSendFollowUp(member.id, 'sms')}
                      disabled={!member.phone || sendFollowUpMutation.isPending}
                    >
                      <Phone className="mr-1 h-3 w-3" />
                      SMS
                    </Button>
                    <Button 
                      size="sm" 
                      variant="outline" 
                      className="text-xs"
                      onClick={() => handleSendFollowUp(member.id, 'email')}
                      disabled={!member.email || sendFollowUpMutation.isPending}
                    >
                      <Mail className="mr-1 h-3 w-3" />
                      Email
                    </Button>
                    <Button 
                      size="sm" 
                      className="church-button-secondary text-xs"
                      onClick={() => handleSendFollowUp(member.id, member.phone ? 'sms' : 'email')}
                      disabled={sendFollowUpMutation.isPending}
                    >
                      <CheckCircle className="mr-1 h-3 w-3" />
                      {sendFollowUpMutation.isPending ? 'Sending...' : 'Mark Contacted'}
                    </Button>
                  </div>
                </div>
              ))}

              {followUpMembers.length === 0 && (
                <div className="text-center py-8">
                  <CheckCircle className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                  <p className="text-slate-500">No follow-ups needed</p>
                </div>
              )}
            </div>

            {/* Follow-up Template */}
            <div className="mt-6 pt-6 border-t border-slate-200">
              <h4 className="font-medium text-slate-900 mb-3">Message Template</h4>
              <Textarea
                value={followUpTemplate}
                onChange={(e) => setFollowUpTemplate(e.target.value)}
                className="church-form-input"
                rows={3}
                placeholder="Enter your follow-up message template..."
              />
              <Button className="church-button-outline mt-3">
                <CheckCircle className="mr-2 h-4 w-4" />
                Update Template
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { AlertCircle, User, UserPlus, Fingerprint } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import FirstTimerForm from "./first-timer-form";
import type { InsertVisitor } from "@shared/schema";

interface FirstTimerCheckInFlowProps {
  onComplete?: (action: "registered" | "cancelled") => void;
  onRegisterMember?: (visitorData: InsertVisitor) => void;
  scannerType?: "device" | "external" | "simulation";
}

export default function FirstTimerCheckInFlow({ 
  onComplete, 
  onRegisterMember,
  scannerType = "device"
}: FirstTimerCheckInFlowProps) {
  const [currentStep, setCurrentStep] = useState<"detection" | "form" | "biometric" | "complete">("detection");
  const [visitorData, setVisitorData] = useState<InsertVisitor | null>(null);

  const handleVisitorFormSubmit = (data: InsertVisitor) => {
    setVisitorData(data);
    setCurrentStep("biometric");
  };

  const handleBiometricEnrollment = () => {
    // After biometric enrollment (optional), we can register as member
    if (visitorData) {
      onRegisterMember?.(visitorData);
    }
    setCurrentStep("complete");
  };

  const handleSkipEnrollment = () => {
    setCurrentStep("complete");
    onComplete?.("registered");
  };

  const handleCancel = () => {
    onComplete?.("cancelled");
  };

  if (currentStep === "form") {
    return (
      <FirstTimerForm 
        onSubmit={handleVisitorFormSubmit}
        onCancel={handleCancel}
      />
    );
  }

  if (currentStep === "biometric") {
    return (
      <div className="max-w-md mx-auto p-6">
        <Card>
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <div className="w-16 h-16 bg-gradient-to-br from-green-500 to-blue-600 rounded-full flex items-center justify-center">
                <Fingerprint className="h-8 w-8 text-white" />
              </div>
            </div>
            <CardTitle>Optional Biometric Registration</CardTitle>
            <p className="text-sm text-slate-600 mt-2">
              Thank you for filling out the visitor form! Would you like to register your fingerprint for faster check-ins in the future?
            </p>
          </CardHeader>

          <CardContent className="space-y-4">
            <Alert>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                This step is completely optional. You can always register your fingerprint later when you visit again.
              </AlertDescription>
            </Alert>

            <div className="flex space-x-3">
              <Button
                onClick={handleBiometricEnrollment}
                className="flex-1 bg-gradient-to-r from-green-500 to-blue-600 hover:from-green-600 hover:to-blue-700"
              >
                <Fingerprint className="h-4 w-4 mr-2" />
                Register Fingerprint
              </Button>

              <Button
                variant="outline"
                onClick={handleSkipEnrollment}
                className="flex-1"
              >
                Skip for Now
              </Button>
            </div>

            <div className="text-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={handleCancel}
                className="text-slate-500"
              >
                Cancel Check-in
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (currentStep === "complete") {
    return (
      <div className="max-w-md mx-auto p-6">
        <Card>
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <div className="w-16 h-16 bg-gradient-to-br from-green-500 to-blue-600 rounded-full flex items-center justify-center">
                <User className="h-8 w-8 text-white" />
              </div>
            </div>
            <CardTitle className="text-green-700">Welcome to Church!</CardTitle>
            <p className="text-sm text-slate-600 mt-2">
              Thank you for visiting us today. Your information has been recorded and someone from our team will follow up with you soon.
            </p>
          </CardHeader>

          <CardContent>
            <Button
              onClick={() => onComplete?.("registered")}
              className="w-full bg-gradient-to-r from-green-500 to-blue-600 hover:from-green-600 hover:to-blue-700"
            >
              Complete Check-in
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Default detection step
  return (
    <div className="max-w-md mx-auto p-6">
      <Card>
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <div className="w-16 h-16 bg-gradient-to-br from-orange-500 to-red-600 rounded-full flex items-center justify-center">
              <AlertCircle className="h-8 w-8 text-white" />
            </div>
          </div>
          <CardTitle>Fingerprint Not Recognized</CardTitle>
          <Badge variant="secondary" className="mt-2">
            First-time Visitor Detection
          </Badge>
        </CardHeader>

        <CardContent className="space-y-4">
          <Alert>
            <User className="h-4 w-4" />
            <AlertDescription>
              We couldn't find your fingerprint in our system. This usually means you're visiting us for the first time. Welcome!
            </AlertDescription>
          </Alert>

          <div className="text-sm text-slate-600 bg-blue-50 p-3 rounded-lg">
            <p className="font-medium mb-1">What happens next?</p>
            <ul className="space-y-1 text-xs">
              <li>â€¢ Fill out a quick visitor form</li>
              <li>â€¢ Share your contact details (optional)</li>
              <li>â€¢ Tell us about prayer requests</li>
              <li>â€¢ Optionally register your fingerprint</li>
            </ul>
          </div>

          <div className="flex space-x-3">
            <Button
              onClick={() => setCurrentStep("form")}
              className="flex-1 bg-gradient-to-r from-[hsl(258,90%,66%)] to-[hsl(271,91%,65%)] hover:from-[hsl(258,85%,61%)] hover:to-[hsl(271,86%,60%)]"
            >
              <UserPlus className="h-4 w-4 mr-2" />
              Fill Visitor Form
            </Button>

            <Button
              variant="outline"
              onClick={handleCancel}
              className="px-6"
            >
              Cancel
            </Button>
          </div>

          <div className="text-center text-xs text-slate-500">
            <p>Already a member? Try scanning your fingerprint again or ask for assistance.</p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

