              <Alert className="border-orange-200 bg-orange-50">
                <AlertDescription>
                  <strong>Fingerprint Updated:</strong> A new fingerprint has been enrolled and will replace the existing one.
                </AlertDescription>
              </Alert>
            )}
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowUpdateConfirmation(false)}>
              Cancel
            </Button>
            <Button onClick={handleConfirmUpdate} disabled={updateMemberMutation.isPending}>
              {updateMemberMutation.isPending ? "Updating..." : "Confirm Update"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Fingerprint Re-enrollment Dialog */}
      <Dialog open={showFingerprintDialog} onOpenChange={setShowFingerprintDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Fingerprint className="h-5 w-5 text-blue-500" />
              Fingerprint Options
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p>
              <strong>{selectedMember?.firstName} {selectedMember?.surname}</strong> already has a fingerprint enrolled.
            </p>
            <div className="space-y-2">
              <Button
                onClick={() => {
                  setShowFingerprintDialog(false);
                  setShowFingerprintEnroll(true);
                }}
                className="w-full justify-start"
                variant="outline"
              >
                <Fingerprint className="h-4 w-4 mr-2" />
                Re-enroll New Fingerprint
              </Button>
              <Button
                onClick={() => setShowFingerprintDialog(false)}
                className="w-full justify-start"
                variant="outline"
              >
                <CheckCircle className="h-4 w-4 mr-2" />
                Keep Existing Fingerprint
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Parent Contact Information Dialog */}
      <Dialog open={showParentContactDialog} onOpenChange={setShowParentContactDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <UserPlus className="h-5 w-5 text-blue-500" />
              Copy Parent's Contact Information?
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            {pendingParentId && (
              <>
                <p>
                  Would you like to copy <strong>{potentialParents.find(p => p.id === pendingParentId)?.firstName} {potentialParents.find(p => p.id === pendingParentId)?.surname}</strong>'s contact information to this {form.getValues("ageGroup")}?
                </p>
                <div className="bg-slate-50 p-4 rounded-lg space-y-2">
                  <p className="text-sm text-slate-700 font-medium">Parent's Contact Details:</p>
                  {potentialParents.find(p => p.id === pendingParentId)?.phone && (
                    <p className="text-sm">ðŸ“ž {potentialParents.find(p => p.id === pendingParentId)?.phone}</p>
                  )}
                  {potentialParents.find(p => p.id === pendingParentId)?.whatsappNumber && (
                    <p className="text-sm">ðŸ“± {potentialParents.find(p => p.id === pendingParentId)?.whatsappNumber}</p>
                  )}
                  {potentialParents.find(p => p.id === pendingParentId)?.address && (
                    <p className="text-sm">ðŸ  {potentialParents.find(p => p.id === pendingParentId)?.address}</p>
                  )}
                </div>
                <p className="text-xs text-slate-500">
                  This will only copy information to empty fields and won't overwrite existing data.
                </p>
              </>
            )}
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => handleCopyParentContact(false)}>
              Skip - Keep Empty
            </Button>
            <Button onClick={() => handleCopyParentContact(true)} className="bg-blue-600 hover:bg-blue-700 text-white">
              Yes, Copy Contact Info
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { 
  Users, 
  Calendar, 
  BarChart3, 
  Cloud, 
  CloudUpload, 
  CloudDownload,
  Fingerprint,
  Settings,
  UserCog,
  History,
  Trash2,
  RotateCcw,
  Save,
  CheckCircle,
  Upload,
  Download,
  FileText,
  AlertCircle
} from "lucide-react";

export default function SettingsTab() {
  const [followUpEnabled, setFollowUpEnabled] = useState(true);
  const [scanSensitivity, setScanSensitivity] = useState("medium");
  const [followUpWeeks, setFollowUpWeeks] = useState("3");
  const [showBulkUploadDialog, setShowBulkUploadDialog] = useState(false);
  const [uploadPreview, setUploadPreview] = useState<any[]>([]);
  const [uploadErrors, setUploadErrors] = useState<string[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showAdminManagement, setShowAdminManagement] = useState(false);
  const [showActivityLog, setShowActivityLog] = useState(false);
  const [adminUsers, setAdminUsers] = useState<any[]>([]);
  const [activityLogs, setActivityLogs] = useState<any[]>([]);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleExportMembers = async () => {
    try {
      const response = await fetch('/api/export/members');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'church_members.csv';
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Export Started",
        description: "Members data is being downloaded",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Please try again",
        variant: "destructive",
      });
    }
  };

  const handleExportAttendance = async () => {
    try {
      // Export all attendance history (last 365 days by default)
      const endDate = new Date().toISOString().split('T')[0];
      const startDate = new Date(Date.now() - 365 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];

      const response = await fetch(`/api/export/attendance?startDate=${startDate}&endDate=${endDate}`);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `attendance_history_${startDate}_to_${endDate}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Export Started",
        description: "Attendance history is being downloaded",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Please try again",
        variant: "destructive",
      });
    }
  };

  const handleExportMonthlyReport = async () => {
    try {
      const currentDate = new Date();
      const month = currentDate.getMonth() + 1;
      const year = currentDate.getFullYear();

      const response = await fetch(`/api/export/monthly-report?month=${month}&year=${year}`);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `monthly_report_${year}_${month.toString().padStart(2, '0')}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Export Started",
        description: "Monthly report is being downloaded",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Please try again",
        variant: "destructive",
      });
    }
  };

  const handleSystemAction = async (action: string) => {
    try {
      switch (action) {
        case "Manual Backup":
          // In a real system, this would trigger a database backup
          await new Promise(resolve => setTimeout(resolve, 2000)); // Simulate backup time
          toast({
            title: "Backup Completed",
            description: "All church data has been backed up to cloud storage",
          });
          break;
        case "Restore from Backup":
          const confirmed = window.confirm("Are you sure you want to restore from backup? This will overwrite current data.");
          if (confirmed) {
            await new Promise(resolve => setTimeout(resolve, 3000)); // Simulate restore time
            toast({
              title: "Restore Completed",
              description: "Data has been restored from the latest backup",
            });
          }
          break;
        case "Reset Member Fingerprint":
          const member = window.prompt("Enter member ID to reset fingerprint:");
          if (member) {
            // In a real system, this would clear the fingerprint data
            toast({
              title: "Fingerprint Reset",
              description: `Fingerprint data cleared for member ${member}`,
            });
          }
          break;
        case "Calibrate Scanner":
          await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate calibration
          toast({
            title: "Scanner Calibrated",
            description: "Fingerprint scanner has been recalibrated successfully",
          });
          break;
        case "Manage Admin Users":
          // Fetch admin users and show management interface
          try {
            const response = await fetch('/api/admin/users');
            const users = await response.json();
            setAdminUsers(users);
            setShowAdminManagement(true);
          } catch (error) {
            toast({
              title: "Failed to Load",
              description: "Could not load admin users",
              variant: "destructive",
            });
          }
          break;
        case "View Activity Log":
          // Generate sample activity logs and show interface
          const logs = [
            { id: 1, timestamp: new Date().toISOString(), user: "admin", action: "User Login", details: "Church Administrator logged in" },
            { id: 2, timestamp: new Date(Date.now() - 3600000).toISOString(), user: "sarah@church.com", action: "Member Check-in", details: "Processed family check-in for Johnson family" },
            { id: 3, timestamp: new Date(Date.now() - 7200000).toISOString(), user: "admin", action: "Export Data", details: "Generated monthly attendance report" },
            { id: 4, timestamp: new Date(Date.now() - 10800000).toISOString(), user: "mark@church.com", action: "View Report", details: "Accessed weekly attendance summary" },
            { id: 5, timestamp: new Date(Date.now() - 14400000).toISOString(), user: "admin", action: "Settings Update", details: "Modified fingerprint sensitivity settings" },
          ];
          setActivityLogs(logs);
          setShowActivityLog(true);
          break;
        case "Save Settings":
          // Save current settings to backend
          toast({
            title: "Settings Saved",
            description: "All system settings have been saved successfully",
          });
          break;
        default:
          toast({
            title: "Action Completed",
            description: `${action} has been completed successfully`,
          });
      }
    } catch (error) {
      toast({
        title: "Action Failed",
        description: `Failed to complete ${action.toLowerCase()}. Please try again.`,
        variant: "destructive",
      });
    }
  };

  const handleDangerousAction = (action: string) => {
    const confirmed = window.confirm(`Are you sure you want to ${action.toLowerCase()}? This action cannot be undone.`);
    if (confirmed) {
      toast({
        title: "Action Completed",
        description: `${action} has been completed`,
      });
    }
  };

  const handleExportActivityLog = () => {
    // Create CSV content with activity log data
    const headers = ['Timestamp', 'User', 'Action', 'Details'];
    const csvRows = [
      headers.join(','),
      ...activityLogs.map(log => [
        `"${new Date(log.timestamp).toLocaleString()}"`,
        `"${log.user}"`,
        `"${log.action}"`,
        `"${log.details}"`
      ].join(','))
    ];

    const csvContent = csvRows.join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');

    if (link.download !== undefined) {
      const url = URL.createObjectURL(blob);
      link.setAttribute('href', url);
      link.setAttribute('download', `activity-log-${new Date().toISOString().split('T')[0]}.csv`);
      link.style.visibility = 'hidden';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      toast({
        title: "Export Successful",
        description: "Activity log has been exported to CSV file",
      });
    }
  };

  const downloadTemplate = () => {
    const headers = [
      'firstName', 'surname', 'title', 'gender', 'ageGroup', 
      'phone', 'email', 'whatsappNumber', 'address', 
      'dateOfBirth', 'weddingAnniversary', 'isCurrentMember'
    ];

    const csvTemplate = headers.join(',') + '\n' +
      'John,Smith,Mr.,male,adult,+234-123-456-7890,john@example.com,+234-987-654-3210,"123 Main St, City",1990-01-15,2020-06-10,true\n' +
      'Jane,Doe,Mrs.,female,adult,+234-123-456-7891,jane@example.com,+234-987-654-3211,"456 Oak Ave, City",1985-05-20,2018-08-15,true';

    const blob = new Blob([csvTemplate], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'member_upload_template.csv';
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);

    toast({
      title: "Template Downloaded",
      description: "CSV template has been downloaded. Fill it out and upload to add members in bulk.",
    });
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const csvText = e.target?.result as string;
        const lines = csvText.split('\n').filter(line => line.trim());
        const headers = lines[0].split(',').map(h => h.trim());

        const data = lines.slice(1).map((line, index) => {
          const values = line.split(',').map(v => v.trim().replace(/^"|"$/g, ''));
          const row: any = { rowNumber: index + 2 };

          headers.forEach((header, i) => {
            row[header] = values[i] || '';
          });

          return row;
        });

        setUploadPreview(data);
        setUploadErrors([]);
        setShowBulkUploadDialog(true);
      } catch (error) {
        toast({
          title: "File Error",
          description: "Could not read the CSV file. Please check the format.",
          variant: "destructive",
        });
      }
    };

    reader.readAsText(file);
  };

  const validateData = (data: any[]) => {
    const errors: string[] = [];

    data.forEach((row, index) => {
      if (!row.firstName) errors.push(`Row ${row.rowNumber}: First name is required`);
      if (!row.surname) errors.push(`Row ${row.rowNumber}: Surname is required`);
      if (row.gender && !['male', 'female'].includes(row.gender)) {
        errors.push(`Row ${row.rowNumber}: Gender must be 'male' or 'female'`);
      }
      if (row.ageGroup && !['child', 'adolescent', 'adult'].includes(row.ageGroup)) {
        errors.push(`Row ${row.rowNumber}: Age group must be 'child', 'adolescent', or 'adult'`);
      }
      if (row.email && !row.email.includes('@')) {
        errors.push(`Row ${row.rowNumber}: Invalid email format`);
      }
    });

    return errors;
  };

  const processBulkUpload = async () => {
    setIsProcessing(true);
    const errors = validateData(uploadPreview);

    if (errors.length > 0) {
      setUploadErrors(errors);
      setIsProcessing(false);
      return;
    }

    try {
      const response = await fetch('/api/members/bulk-upload', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ members: uploadPreview }),
      });

      const result = await response.json();

      if (response.ok) {
        toast({
          title: "Upload Successful",
          description: `${result.created} members added successfully`,
        });
        setShowBulkUploadDialog(false);
        setUploadPreview([]);
        if (fileInputRef.current) fileInputRef.current.value = '';
      } else {
        throw new Error(result.error || 'Upload failed');
      }
    } catch (error) {
      toast({
        title: "Upload Failed",
        description: error instanceof Error ? error.message : "Please try again",
        variant: "destructive",
      });
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="space-y-8">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Data Management */}
        <Card className="church-card">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-slate-900">Data Management</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-6">
              <div>
                <h4 className="font-medium text-slate-900 mb-3">Export Options</h4>
                <div className="space-y-3">
                  <Button 
                    onClick={handleExportMembers}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <Users className="mr-3 h-4 w-4" />
                    Export All Members (CSV)
                  </Button>
                  <Button 
                    onClick={handleExportAttendance}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <Calendar className="mr-3 h-4 w-4" />
                    Export Attendance History (CSV)
                  </Button>
                  <Button 
                    onClick={handleExportMonthlyReport}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <BarChart3 className="mr-3 h-4 w-4" />
                    Export Monthly Report (CSV)
                  </Button>
                </div>
              </div>

              <div className="border-t border-slate-200 pt-6">
                <h4 className="font-medium text-slate-900 mb-3">Bulk Import Options</h4>
                <div className="space-y-3">
                  <Button 
                    onClick={downloadTemplate}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <Download className="mr-3 h-4 w-4" />
                    Download CSV Template
                  </Button>
                  <div>
                    <input
                      ref={fileInputRef}
                      type="file"
                      accept=".csv"
                      onChange={handleFileUpload}
                      className="hidden"
                      id="bulk-upload"
                    />
                    <Button 
                      onClick={() => fileInputRef.current?.click()}
                      variant="outline" 
                      className="w-full justify-start"
                    >
                      <Upload className="mr-3 h-4 w-4" />
                      Upload Members (CSV)
                    </Button>
                  </div>
                </div>
              </div>

              <div className="border-t border-slate-200 pt-6">
                <h4 className="font-medium text-slate-900 mb-3">Backup & Sync</h4>
                <div className="space-y-3">
                  <div className="flex items-center justify-between p-3 bg-[hsl(142,76%,36%)]/5 border border-[hsl(142,76%,36%)]/20 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <Cloud className="text-[hsl(142,76%,36%)] h-4 w-4" />
                      <span className="text-sm">Google Sheets Sync</span>
                    </div>
                    <span className="text-xs text-[hsl(142,76%,36%)]">Connected</span>
                  </div>
                  <Button 
                    onClick={() => handleSystemAction("Manual Backup")}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <CloudUpload className="mr-3 h-4 w-4" />
                    Manual Backup to Cloud
                  </Button>
                  <Button 
                    onClick={() => handleSystemAction("Restore from Backup")}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <CloudDownload className="mr-3 h-4 w-4" />
                    Restore from Backup
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* System Settings */}
        <Card className="church-card">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-slate-900">System Settings</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-6">
              <div>
                <h4 className="font-medium text-slate-900 mb-3">Fingerprint Settings</h4>
                <div className="space-y-3">
                  <Button 
                    onClick={() => handleSystemAction("Reset Member Fingerprint")}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <Fingerprint className="mr-3 h-4 w-4" />
                    Reset Member Fingerprint
                  </Button>
                  <Button 
                    onClick={() => handleSystemAction("Calibrate Scanner")}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <Settings className="mr-3 h-4 w-4" />
                    Calibrate Scanner
                  </Button>
                  <div className="flex items-center justify-between">
                    <Label htmlFor="scan-sensitivity" className="text-sm text-slate-700">
                      Auto Check-in Sensitivity
                    </Label>
                    <Select value={scanSensitivity} onValueChange={setScanSensitivity}>
                      <SelectTrigger className="w-32">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="low">Low</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              </div>

              <div className="border-t border-slate-200 pt-6">
                <h4 className="font-medium text-slate-900 mb-3">Follow-up Settings</h4>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <Label htmlFor="followup-weeks" className="text-sm text-slate-700">
                      Auto Follow-up After
                    </Label>
                    <Select value={followUpWeeks} onValueChange={setFollowUpWeeks}>
                      <SelectTrigger className="w-32">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="2">2 weeks</SelectItem>
                        <SelectItem value="3">3 weeks</SelectItem>
                        <SelectItem value="4">4 weeks</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="flex items-center justify-between">
                    <Label htmlFor="followup-enabled" className="text-sm text-slate-700">
                      Send Follow-ups
                    </Label>
                    <Switch
                      id="followup-enabled"
                      checked={followUpEnabled}
                      onCheckedChange={setFollowUpEnabled}
                    />
                  </div>
                </div>
              </div>

              <div className="border-t border-slate-200 pt-6">
                <h4 className="font-medium text-slate-900 mb-3">Admin Access</h4>
                <div className="space-y-3">
                  <Button 
                    onClick={() => handleSystemAction("Manage Admin Users")}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <UserCog className="mr-3 h-4 w-4" />
                    Manage Admin Users
                  </Button>
                  <Button 
                    onClick={() => handleSystemAction("View Activity Log")}
                    variant="outline" 
                    className="w-full justify-start"
                  >
                    <History className="mr-3 h-4 w-4" />
                    View Activity Log
                  </Button>
                </div>
              </div>

              <div className="pt-4">
                <Button 
                  onClick={() => handleSystemAction("Save Settings")}
                  className="church-button-primary w-full"
                >
                  <Save className="mr-2 h-4 w-4" />
                  Save Settings
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Danger Zone */}
      <Card className="bg-[hsl(0,84%,60%)]/5 border border-[hsl(0,84%,60%)]/20">
        <CardHeader>
          <CardTitle className="text-lg font-semibold text-[hsl(0,84%,60%)]">Danger Zone</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-sm text-slate-600 mb-4">
            These actions cannot be undone. Please be careful.
          </p>
          <div className="flex space-x-4">
            <Button 
              onClick={() => handleDangerousAction("Clear All Data")}
              className="church-button-error"
            >
              <Trash2 className="mr-2 h-4 w-4" />
              Clear All Data
            </Button>
            <Button 
              onClick={() => handleDangerousAction("Factory Reset")}
              className="church-button-error"
            >
              <RotateCcw className="mr-2 h-4 w-4" />
              Factory Reset
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Bulk Upload Dialog */}
      <Dialog open={showBulkUploadDialog} onOpenChange={setShowBulkUploadDialog}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Bulk Member Upload Preview</DialogTitle>
            <DialogDescription>
              Review the data below before uploading. {uploadPreview.length} members ready to be added.
            </DialogDescription>
          </DialogHeader>

          {uploadErrors.length > 0 && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-4">
              <div className="flex items-center space-x-2 text-red-800 font-medium mb-2">
                <AlertCircle className="h-4 w-4" />
                <span>Validation Errors</span>
              </div>
              <ul className="text-sm text-red-700 space-y-1">
                {uploadErrors.map((error, index) => (
                  <li key={index}>â€¢ {error}</li>
                ))}
              </ul>
            </div>
          )}

          <div className="border rounded-lg">
            <div className="max-h-96 overflow-auto">
              <table className="w-full text-sm">
                <thead className="bg-slate-50 sticky top-0">
                  <tr>
                    <th className="p-2 text-left border-b">Name</th>
                    <th className="p-2 text-left border-b">Gender</th>
                    <th className="p-2 text-left border-b">Age Group</th>
                    <th className="p-2 text-left border-b">Phone</th>
                    <th className="p-2 text-left border-b">Email</th>
                  </tr>
                </thead>
                <tbody>
                  {uploadPreview.map((row, index) => (
                    <tr key={index} className="border-b">
                      <td className="p-2">{row.firstName} {row.surname}</td>
                      <td className="p-2">{row.gender}</td>
                      <td className="p-2">{row.ageGroup}</td>
                      <td className="p-2">{row.phone}</td>
                      <td className="p-2">{row.email}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <Button 
              variant="outline" 
              onClick={() => setShowBulkUploadDialog(false)}
              disabled={isProcessing}
            >
              Cancel
            </Button>
            <Button 
              onClick={processBulkUpload}
              disabled={isProcessing || uploadErrors.length > 0}
              className="bg-[hsl(142,76%,36%)] hover:bg-[hsl(142,76%,30%)]"
            >
              {isProcessing ? "Uploading..." : `Upload ${uploadPreview.length} Members`}
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Admin Users Management Dialog */}
      <Dialog open={showAdminManagement} onOpenChange={setShowAdminManagement}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Admin User Management</DialogTitle>
            <DialogDescription>
              Manage system administrators, volunteers, and data viewers. {adminUsers.length} users found.
            </DialogDescription>
          </DialogHeader>

          <div className="border rounded-lg">
            <div className="max-h-96 overflow-auto">
              <table className="w-full text-sm">
                <thead className="bg-slate-50 sticky top-0">
                  <tr>
                    <th className="p-3 text-left border-b">Name</th>
                    <th className="p-3 text-left border-b">Email</th>
                    <th className="p-3 text-left border-b">Role</th>
                    <th className="p-3 text-left border-b">Region</th>
                    <th className="p-3 text-left border-b">Status</th>
                    <th className="p-3 text-left border-b">Last Login</th>
                  </tr>
                </thead>
                <tbody>
                  {adminUsers.map((user) => (
                    <tr key={user.id} className="border-b hover:bg-slate-50">
                      <td className="p-3 font-medium">{user.fullName}</td>
                      <td className="p-3">{user.email}</td>
                      <td className="p-3">
                        <span className={`px-2 py-1 rounded text-xs font-medium ${
                          user.role === 'admin' ? 'bg-red-100 text-red-800' :
                          user.role === 'volunteer' ? 'bg-blue-100 text-blue-800' :
                          'bg-green-100 text-green-800'
                        }`}>
                          {user.role === 'admin' ? 'Administrator' :
                           user.role === 'volunteer' ? 'Volunteer' : 'Data Viewer'}
                        </span>
                      </td>
                      <td className="p-3">{user.region}</td>
                      <td className="p-3">
                        <span className={`px-2 py-1 rounded text-xs font-medium ${
                          user.isActive ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
                        }`}>
                          {user.isActive ? 'Active' : 'Inactive'}
                        </span>
                      </td>
                      <td className="p-3 text-gray-600">
                        {user.lastLogin ? new Date(user.lastLogin).toLocaleDateString() : 'Never'}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <Button variant="outline" onClick={() => setShowAdminManagement(false)}>
              Close
            </Button>
            <Button className="bg-[hsl(142,76%,36%)] hover:bg-[hsl(142,76%,30%)]">
              Add New User
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Activity Log Dialog */}
      <Dialog open={showActivityLog} onOpenChange={setShowActivityLog}>
        <DialogContent className="max-w-5xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>System Activity Log</DialogTitle>
            <DialogDescription>
              Recent system activities and user actions. Showing last {activityLogs.length} activities.
            </DialogDescription>
          </DialogHeader>

          <div className="border rounded-lg">
            <div className="max-h-96 overflow-auto">
              <table className="w-full text-sm">
                <thead className="bg-slate-50 sticky top-0">
                  <tr>
                    <th className="p-3 text-left border-b">Timestamp</th>
                    <th className="p-3 text-left border-b">User</th>
                    <th className="p-3 text-left border-b">Action</th>
                    <th className="p-3 text-left border-b">Details</th>
                  </tr>
                </thead>
                <tbody>
                  {activityLogs.map((log) => (
                    <tr key={log.id} className="border-b hover:bg-slate-50">
                      <td className="p-3 font-mono text-xs">
                        {new Date(log.timestamp).toLocaleString()}
                      </td>
                      <td className="p-3 font-medium">{log.user}</td>
                      <td className="p-3">
                        <span className={`px-2 py-1 rounded text-xs font-medium ${
                          log.action.includes('Login') ? 'bg-blue-100 text-blue-800' :
                          log.action.includes('Check-in') ? 'bg-green-100 text-green-800' :
                          log.action.includes('Export') ? 'bg-purple-100 text-purple-800' :
                          log.action.includes('Settings') ? 'bg-orange-100 text-orange-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {log.action}
                        </span>
                      </td>
                      <td className="p-3 text-gray-600">{log.details}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <Button variant="outline" onClick={() => setShowActivityLog(false)}>
              Close
            </Button>
            <Button 
              className="bg-[hsl(142,76%,36%)] hover:bg-[hsl(142,76%,30%)]"
              onClick={handleExportActivityLog}
            >
              Export Log
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { insertVisitorSchema, type InsertVisitor, type Visitor } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { User, Phone, Mail, Calendar, Heart, MessageSquare, Filter, Users, UserCheck, Clock, Search, Edit, Plus, UserPlus, Save, X, Download } from "lucide-react";

export default function VisitorsTab() {
  const [filterStatus, setFilterStatus] = useState<"all" | "pending" | "contacted" | "member">("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedVisitor, setSelectedVisitor] = useState<Visitor | null>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editNotes, setEditNotes] = useState("");
  const [editStatus, setEditStatus] = useState<"pending" | "contacted" | "member">("pending");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Export function
  const handleExportVisitors = async () => {
    try {
      const response = await fetch('/api/export/visitors');
      if (!response.ok) {
        throw new Error('Export failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;

      const date = new Date().toISOString().split('T')[0];
      a.download = `visitors_export_${date}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Success",
        description: "Visitors data exported successfully",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to export visitors data",
        variant: "destructive",
      });
    }
  };

  // Form for adding new visitors
  const form = useForm<InsertVisitor>({
    resolver: zodResolver(insertVisitorSchema),
    defaultValues: {
      name: "",
      gender: undefined,
      ageGroup: undefined,
      address: "",
      email: "",
      phone: "",
      whatsappNumber: "",
      weddingAnniversary: "",
      birthday: "",
      prayerPoints: "",
      howDidYouHearAboutUs: "",
      comments: "",
      followUpStatus: "pending",
      assignedTo: "",
    },
  });

  // Form for editing visitors
  const editForm = useForm<InsertVisitor>({
    resolver: zodResolver(insertVisitorSchema),
    defaultValues: {
      name: "",
      gender: undefined,
      ageGroup: undefined,
      address: "",
      email: "",
      phone: "",
      whatsappNumber: "",
      weddingAnniversary: "",
      birthday: "",
      prayerPoints: "",
      howDidYouHearAboutUs: "",
      comments: "",
      followUpStatus: "pending",
      assignedTo: "",
    },
  });

  // Fetch visitors data
  const { data: visitors = [], isLoading } = useQuery<Visitor[]>({
    queryKey: ["/api/visitors"],
  });

  // Create visitor mutation - now includes check-in
  const createVisitorMutation = useMutation({
    mutationFn: async (data: InsertVisitor) => {
      const response = await apiRequest('POST', '/api/visitor-checkin', data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/visitors'] });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      toast({
        title: "Success",
        description: "Visitor checked in successfully! They are now included in today's attendance.",
      });
      form.reset();
      setIsAddDialogOpen(false);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to check in visitor",
        variant: "destructive",
      });
    },
  });

  // Update visitor mutation
  const updateVisitorMutation = useMutation({
    mutationFn: async (data: { id: string; updates: Partial<Visitor> }) => {
      const response = await fetch(`/api/visitors/${data.id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data.updates),
      });

      if (!response.ok) {
        throw new Error("Failed to update visitor");
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Visitor information updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/visitors"] });
      setIsEditDialogOpen(false);
      setSelectedVisitor(null);
      editForm.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update visitor",
        variant: "destructive",
      });
    },
  });

  // Filter visitors based on status and search
  const filteredVisitors = visitors.filter((visitor: Visitor) => {
    const matchesStatus = filterStatus === "all" || visitor.followUpStatus === filterStatus;
    const matchesSearch = searchQuery === "" || 
      visitor.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (visitor.email && visitor.email.toLowerCase().includes(searchQuery.toLowerCase())) ||
      (visitor.phone && visitor.phone.includes(searchQuery));

    return matchesStatus && matchesSearch;
  });

  const handleEditVisitor = (visitor: Visitor) => {
    setSelectedVisitor(visitor);
    // Pre-populate the edit form with visitor data
    editForm.reset({
      name: visitor.name || "",
      gender: visitor.gender as "male" | "female" | undefined,
      ageGroup: visitor.ageGroup as "child" | "adolescent" | "adult" | undefined,
      address: visitor.address || "",
      email: visitor.email || "",
      phone: visitor.phone || "",
      whatsappNumber: visitor.whatsappNumber || "",
      weddingAnniversary: visitor.weddingAnniversary || "",
      birthday: visitor.birthday || "",
      prayerPoints: visitor.prayerPoints || "",
      howDidYouHearAboutUs: visitor.howDidYouHearAboutUs || "",
      comments: visitor.comments || "",
      followUpStatus: visitor.followUpStatus as "pending" | "contacted" | "member" || "pending",
      assignedTo: visitor.assignedTo || "",
    });
    setIsEditDialogOpen(true);
  };

  const onSubmit = (data: InsertVisitor) => {
    // Ensure optional fields are handled properly
    const cleanedData = {
      ...data,
      address: data.address || "",
      email: data.email || "",
      phone: data.phone || "",
      whatsappNumber: data.whatsappNumber || "",
      prayerPoints: data.prayerPoints || "",
      howDidYouHearAboutUs: data.howDidYouHearAboutUs || "",
      comments: data.comments || "",
      assignedTo: data.assignedTo || "",
      weddingAnniversary: data.weddingAnniversary || "",
      birthday: data.birthday || "",
    };
    createVisitorMutation.mutate(cleanedData);
  };

  const onEditSubmit = (data: InsertVisitor) => {
    if (!selectedVisitor) return;

    // Clean the data similar to create mutation
    const cleanedData = {
      ...data,
      address: data.address || "",
      email: data.email || "",
      phone: data.phone || "",
      whatsappNumber: data.whatsappNumber || "",
      prayerPoints: data.prayerPoints || "",
      howDidYouHearAboutUs: data.howDidYouHearAboutUs || "",
      comments: data.comments || "",
      assignedTo: data.assignedTo || "",
      weddingAnniversary: data.weddingAnniversary || "",
      birthday: data.birthday || "",
    };

    updateVisitorMutation.mutate({
      id: selectedVisitor.id,
      updates: cleanedData,
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">Pending</Badge>;
      case "contacted":
        return <Badge variant="secondary" className="bg-blue-100 text-blue-800">Contacted</Badge>;
      case "member":
        return <Badge variant="secondary" className="bg-green-100 text-green-800">Member</Badge>;
      default:
        return <Badge variant="secondary">Unknown</Badge>;
    }
  };

  const statusCounts = visitors.reduce((acc: Record<string, number>, visitor: Visitor) => {
    const status = visitor.followUpStatus || 'pending';
    acc[status] = (acc[status] || 0) + 1;
    return acc;
  }, {});

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-[hsl(258,90%,66%)] mx-auto"></div>
          <p className="mt-2 text-sm text-slate-600">Loading visitors...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Users className="h-5 w-5 text-slate-500" />
              <div>
                <p className="text-sm font-medium text-slate-600">Total Visitors</p>
                <p className="text-2xl font-bold">{visitors.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Clock className="h-5 w-5 text-yellow-500" />
              <div>
                <p className="text-sm font-medium text-slate-600">Pending Follow-up</p>
                <p className="text-2xl font-bold">{statusCounts.pending || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Phone className="h-5 w-5 text-blue-500" />
              <div>
                <p className="text-sm font-medium text-slate-600">Contacted</p>
                <p className="text-2xl font-bold">{statusCounts.contacted || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <UserCheck className="h-5 w-5 text-green-500" />
              <div>
                <p className="text-sm font-medium text-slate-600">Became Members</p>
                <p className="text-2xl font-bold">{statusCounts.member || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Actions Bar */}
      <div className="flex flex-col sm:flex-row justify-between gap-4">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex items-center space-x-2">
            <Search className="h-4 w-4 text-slate-500" />
            <Input
              placeholder="Search visitors..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full sm:w-64"
            />
          </div>

          <div className="flex items-center space-x-2">
            <Filter className="h-4 w-4 text-slate-500" />
            <Select value={filterStatus} onValueChange={(value: any) => setFilterStatus(value)}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Visitors</SelectItem>
                <SelectItem value="pending">Pending Follow-up</SelectItem>
                <SelectItem value="contacted">Contacted</SelectItem>
                <SelectItem value="member">Became Members</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        <div className="flex gap-2">
          <Button 
            onClick={handleExportVisitors}
            variant="outline"
            className="border-[hsl(258,90%,66%)] text-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,66%)] hover:text-white"
          >
            <Download className="h-4 w-4 mr-2" />
            Export Visitors
          </Button>
          <Button 
            onClick={() => setIsAddDialogOpen(true)} 
            className="bg-[hsl(258,90%,66%)] hover:bg-[hsl(258,90%,60%)] text-white"
          >
            <UserPlus className="h-4 w-4 mr-2" />
            Add Visitor
          </Button>
        </div>
      </div>

      {/* Visitors Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Users className="h-5 w-5" />
            <span>First-Time Visitors ({filteredVisitors.length})</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {filteredVisitors.length === 0 ? (
            <div className="text-center py-8">
              <Users className="h-12 w-12 text-slate-300 mx-auto mb-4" />
              <p className="text-slate-500">No visitors found matching your criteria.</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Name</TableHead>
                    <TableHead>Demographics</TableHead>
                    <TableHead>Contact</TableHead>
                    <TableHead>Visit Date</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Prayer Points</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredVisitors.map((visitor: Visitor) => (
                    <TableRow key={visitor.id}>
                      <TableCell>
                        <div>
                          <p className="font-medium">{visitor.name}</p>
                          {visitor.howDidYouHearAboutUs && (
                            <p className="text-xs text-slate-500">
                              Heard about us: {visitor.howDidYouHearAboutUs}
                            </p>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex flex-col space-y-1">
                          {visitor.gender && (
                            <Badge variant="outline" className="capitalize">
                              {visitor.gender}
                            </Badge>
                          )}
                          {visitor.ageGroup && (
                            <Badge variant="secondary" className="capitalize">
                              {visitor.ageGroup}
                            </Badge>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="space-y-1">
                          {visitor.phone && (
                            <div className="flex items-center space-x-1 text-sm">
                              <Phone className="h-3 w-3" />
                              <span>{visitor.phone}</span>
                            </div>
                          )}
                          {visitor.email && (
                            <div className="flex items-center space-x-1 text-sm">
                              <Mail className="h-3 w-3" />
                              <span>{visitor.email}</span>
                            </div>
                          )}
                          {visitor.whatsappNumber && (
                            <div className="flex items-center space-x-1 text-sm text-green-600">
                              <MessageSquare className="h-3 w-3" />
                              <span>WhatsApp: {visitor.whatsappNumber}</span>
                            </div>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center space-x-1 text-sm">
                          <Calendar className="h-3 w-3" />
                          <span>{new Date(visitor.visitDate!).toLocaleDateString()}</span>
                        </div>
                      </TableCell>
                      <TableCell>{getStatusBadge(visitor.followUpStatus!)}</TableCell>
                      <TableCell>
                        {visitor.prayerPoints ? (
                          <div className="flex items-center space-x-1">
                            <Heart className="h-3 w-3 text-red-500" />
                            <span className="text-sm truncate max-w-32" title={visitor.prayerPoints}>
                              {visitor.prayerPoints}
                            </span>
                          </div>
                        ) : (
                          <span className="text-slate-400 text-sm">None</span>
                        )}
                      </TableCell>
                      <TableCell>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleEditVisitor(visitor)}
                        >
                          <Edit className="h-3 w-3 mr-1" />
                          Edit
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Visitor Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle className="flex items-center space-x-2">
              <Edit className="h-5 w-5 text-[hsl(258,90%,66%)]" />
              <span>Edit Visitor Information</span>
            </DialogTitle>
          </DialogHeader>

          {selectedVisitor && (
            <Form {...editForm}>
              <form onSubmit={editForm.handleSubmit(onEditSubmit)} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {/* Name Field */}
                  <FormField
                    control={editForm.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name *</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter full name" {...field} />
