                  type="password"
                  {...register('password')}
                  placeholder="Enter your password"
                  className={errors.password ? 'border-red-500' : ''}
                  autoComplete="current-password"
                />
                {errors.password && (
                  <p className="text-sm text-red-500 mt-1">{errors.password.message}</p>
                )}
